import React, { useState } from 'react';
import { motion } from 'framer-motion';

export default function Welcome() {
  const [showIntro, setShowIntro] = useState(true);

  const handleNext = () => {
    setShowIntro(false);
  };

  const handleBack = () => {
    setShowIntro(true);
  };

  const fadeIn = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { duration: 0.5 } }
  };

  const slideIn = {
    hidden: { x: '-100vw' },
    visible: { x: 0, transition: { type: 'spring', stiffness: 120 } }
  };

  return (
    <div className="flex items-center min-h-screen">
      <motion.div
        id="welcome"
        className="mx-10"
        initial="hidden"
        animate="visible"
        variants={fadeIn}
      >
        {showIntro ? (
          <motion.div
            id="intro"
            className="transition-opacity duration-500 ease-in-out"
            variants={slideIn}
          >
            <motion.h1
              className="gradient-text font-bold text-4xl"
              initial={{ y: -20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.2 }}
            >
              Hey there, RRISD student!
            </motion.h1>
            <motion.p
              className="text-white min-w-60 w-5/12 mb-5"
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.4 }}
            >
              Congratulations, you're about to be a high schooler! Before you
              transition, it is vital to understand what your passion is and how
              you will move forward with our career. We're going to do something
              called a <b>four year plan</b>.
            </motion.p>

            <motion.button
              onClick={handleNext}
              className="text-white bg-emerald-500 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              <span className="relative z-10 transition-all duration-500 group-hover:pr-6">
                NEXT
              </span>
              <span className="absolute right-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:right-4">
                →
              </span>
            </motion.button>
          </motion.div>
        ) : (
          <motion.div
            id="nextContent"
            className="transition-opacity duration-500 ease-in-out"
            variants={slideIn}
          >
            <motion.h2
              className="gradient-text font-bold text-4xl"
              initial={{ y: -20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.2 }}
            >
              Let's start your four year plan!
            </motion.h2>
            <motion.p
              className="text-white min-w-60 w-5/12 mb-5"
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.4 }}
            >
              Here you can add more content about the four year plan or any other information you want to show after clicking 'NEXT'.
            </motion.p>
            <motion.button
              onClick={handleBack}
              className="text-white bg-emerald-500 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              <span className="relative z-10 transition-all duration-500 group-hover:pl-6">
                BACK
              </span>
              <span className="absolute left-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:left-4">
                ←
              </span>
            </motion.button>
          </motion.div>
        )}
      </motion.div>
    </div>
  );
}
