import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Welcome from "./components/Welcome.js";
import Build from "./components/Build.js";
import Header from "./components/Header.js";
import Footer from "./components/Footer.js";
import PulseLoader from "react-spinners/PulseLoader";
import { useState, useEffect } from "react";

function App() {
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 2000); // Adjust this value as needed
  }, []);

  return (
    <Router>
      <div className="App bg-neutral-900 min-h-screen flex flex-col">
        {loading ? (
          <div className="flex-grow flex items-center justify-center">
            <PulseLoader color={"#D45F29"} loading={loading} size={20} />
          </div>
        ) : (
          <>
            <Header />
            <div className="flex-grow">
              <Routes>
                <Route path="/intro" element={<Welcome />} />
                <Route path="/build" element={<Build />} />
              </Routes>
            </div>
            <Footer />
          </>
        )}
      </div>
    </Router>
  );
}

export default App;
