import React, { useState } from 'react';
import OpenAI from 'openai';
import './Tips.css'; // Assuming you'll create a separate CSS file

const Tips = () => {
  const [userInput, setUserInput] = useState('');
  const [chatbotResponse, setChatbotResponse] = useState('');

  const openai = new OpenAI({
    apiKey: 'sk-la8GDlMXteiPcwykGJT76wSzhAHmxeMJOg94K4Tgl9T3BlbkFJuzdz0x6rC3BFZlwH8EjnnVYQTdTvgn739E5q0bvrIA',
    dangerouslyAllowBrowser: true
  });

  const handleInputChange = (e) => {
    setUserInput(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await openai.chat.completions.create({
        model: "gpt-3.5-turbo",
        messages: [
          { role: "system", content: "You are a helpful assistant that provides advice on course selection for high school students." },
          { role: "user", content: userInput }
        ],
      });
      setChatbotResponse(response.choices[0].message.content);
    } catch (error) {
      console.error('Error:', error);
      setChatbotResponse('Sorry, there was an error processing your request.');
    }
  };

  const schoolTips = [
    "Consider taking AP classes if you're looking for college credit opportunities.",
    "IB programs offer a more holistic approach to education compared to AP.",
    "Balance your course load between challenging classes and maintaining a good GPA.",
    "Explore electives that align with your interests and potential career paths.",
    "Don't forget to include extracurricular activities in your schedule."
  ];

  return (
    <div className="tips-container">
      <h2 className="tips-header">School Tips</h2>
      <ul className="tips-list">
        {schoolTips.map((tip, index) => (
          <li key={index} className="tip-item">{tip}</li>
        ))}
      </ul>
      <h2 className="assistant-header">Course Selection Assistant</h2>
      <form onSubmit={handleSubmit} className="assistant-form">
        <input
          type="text"
          value={userInput}
          onChange={handleInputChange}
          placeholder="Ask about course selection..."
          className="assistant-input"
        />
        <button type="submit" className="assistant-button">Submit</button>
      </form>
      {chatbotResponse && (
        <div className="chatbot-response">
          <h3 className="response-header">Assistant:</h3>
          <p className="response-content">{chatbotResponse}</p>
        </div>
      )}
    </div>
  );
};

export default Tips;
