import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

export default function Welcome() {
  const [checkpoint, setCheckpoint] = useState(0);

  const handleNext = () => {
    setCheckpoint((prev) => Math.min(prev + 1, 3));
  };

  const handleBack = () => {
    setCheckpoint((prev) => Math.max(prev - 1, 0));
  };

  const fadeIn = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { duration: 0.5 } },
  };

  const checkpoints = [
    {
      title: "Hey there, RRISD student!",
      content: "Congratulations, you're about to be a high schooler! Before you transition, it is vital to understand what your passion is and how you will move forward with your career. We're going to do something called a <b>four year plan</b>.",
    },
    {
      title: "What is a four year plan?",
      content: "As you progress through high school, you'll need to know which courses seem to be a good fit for you. Additionally, a four year plan can map out what you will do for your freshman, sophomore, junior, and senior year. Take a look at the example on the right!",
    },
    {
      title: "Why is a four year plan important?",
      content: "A four year plan helps you stay organized and focused on your academic and career goals. It ensures you meet graduation requirements and take courses that align with your interests and future aspirations.",
    }
  ];

  return (
    <div className="flex items-center min-h-screen">
      <motion.div
        id="welcome"
        className="mx-10"
        initial="hidden"
        animate="visible"
        variants={fadeIn}
      >
        <AnimatePresence mode="wait">
          <motion.div
            key={checkpoint}
            id={`checkpoint-${checkpoint}`}
            className="transition-opacity duration-500 ease-in-out"
            initial="hidden"
            animate="visible"
            exit="hidden"
            variants={fadeIn}
          >
            <motion.h1
              className="gradient-text font-bold text-4xl"
              variants={fadeIn}
            >
              {checkpoints[checkpoint].title}
            </motion.h1>
            <motion.p
              className="text-white min-w-60 w-5/12 mb-5"
              variants={fadeIn}
              dangerouslySetInnerHTML={{ __html: checkpoints[checkpoint].content }}
            />
          </motion.div>
        </AnimatePresence>
        <div className="flex space-x-4">
          {checkpoint > 0 && (
            <button
              onClick={handleBack}
              className="text-white bg-emerald-500 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden"
            >
              <span className="relative z-10 transition-all duration-500 group-hover:pl-6">
                BACK
              </span>
              <span className="absolute left-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:left-4">
                ‚Üê
              </span>
            </button>
          )}
          {checkpoint < checkpoints.length - 1 && (
            <button
              onClick={handleNext}
              className="text-white bg-emerald-500 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden"
            >
              <span className="relative z-10 transition-all duration-500 group-hover:pr-6">
                NEXT
              </span>
              <span className="absolute right-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:right-4">
                ‚Üí
              </span>
            </button>
          )}
        </div>
      </motion.div>
    </div>
  );
}
