import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import courseData from "../assets/data/other-three.json";

const SearchBar = ({ onSearch, onFilter }) => {
const [searchTerm, setSearchTerm] = useState('');
const [filterOption, setFilterOption] = useState('all');
const [isDropdownOpen, setIsDropdownOpen] = useState(false);

const handleSearch = (e) => {
  onSearch(e.target.value);
};

const handleFilter = (option) => {
  setFilterOption(option);
  onFilter(option);
  setIsDropdownOpen(false);
};

const filterOptions = [
  { value: 'all', label: 'All' },
  { value: 'Elective', label: 'Electives' },
  { value: 'Math', label: 'Math' },
  { value: 'English', label: 'English' },
  { value: 'Science', label: 'Science' },
  { value: 'Social Studies', label: 'Social Studies' },
  { value: 'credits-low-high', label: 'Credits (Low to High)' },
  { value: 'credits-high-low', label: 'Credits (High to Low)' },
];

return (
  <div className="w-11/12 sm:w-4/6 mt-10 mb-6 flex items-center">
    <div className="relative flex-grow mr-4">
      <div className="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
        <svg
          className="w-4 h-4 text-gray-500 dark:text-gray-400"
          aria-hidden="true"
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 20 20"
        >
          <path
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            d="m19 19-4-4m0-7A7 7 0 1 1 1 8a7 7 0 0 1 14 0Z"
          />
        </svg>
      </div>
      <input
        type="search"
        id="default-search"
        className="block w-full p-4 ps-10 text-sm text-gray-50 shadow-2xl rounded-lg bg-neutral-800"
        placeholder='Search for courses here...'
        value={searchTerm}
        onChange={(e) => {
          setSearchTerm(e.target.value);
          handleSearch(e);
        }}
      />
    </div>
    <div className="relative">
      <button
        className="p-4 text-sm text-gray-50 shadow-2xl rounded-lg bg-neutral-800 flex items-center"
        onClick={() => setIsDropdownOpen(!isDropdownOpen)}
      >
        {filterOptions.find(option => option.value === filterOption)?.label || 'Filter'}
        <svg className={`w-4 h-4 ml-2 transition-transform duration-300 ${isDropdownOpen ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
        </svg>
      </button>
      {isDropdownOpen && (
        <div className="absolute right-0 mt-2 w-48 rounded-2xl shadow-lg bg-neutral-800 ring-1 ring-black ring-opacity-5">
          <div className="py-1" role="menu" aria-orientation="vertical" aria-labelledby="options-menu">
            {filterOptions.map((option) => (
              <button
                key={option.value}
                className="block w-full text-left px-4 py-2 text-sm text-gray-50 hover:bg-neutral-700"
                onClick={() => handleFilter(option.value)}
              >
                {option.label}
              </button>
            ))}
          </div>
        </div>
      )}
    </div>
  </div>
);
};

const Courses = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterOption, setFilterOption] = useState('all');
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
      const allCourses = courseData["sophomore-year"].flatMap(department => 
          department.courses.map(course => ({...course, department: department.department}))
      );
      let filtered = allCourses.filter(course =>
          course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          course.number.toLowerCase().includes(searchTerm.toLowerCase())
      );

      if (filterOption !== 'all' && filterOption !== 'credits-low-high' && filterOption !== 'credits-high-low') {
          filtered = filtered.filter(course => course.department === filterOption);
      }

      if (filterOption === 'credits-low-high') {
          filtered.sort((a, b) => a.credits - b.credits);
      } else if (filterOption === 'credits-high-low') {
          filtered.sort((a, b) => b.credits - a.credits);
      }

      setFilteredCourses(filtered);
  }, [searchTerm, filterOption]);

  const handleSearch = (term) => {
      setSearchTerm(term);
  };

  const handleFilter = (option) => {
      setFilterOption(option);
  };

  const handleCourseClick = (course) => {
      setSelectedCourse(course);
  };

  const getBadgeColor = (department) => {
      switch(department) {
          case 'Math': return 'bg-blue-100 text-blue-800 ring-blue-800/20';
          case 'Science': return 'bg-green-100 text-green-800 ring-green-700/30';
          case 'Social Studies': return 'bg-yellow-100 text-yellow-900 ring-yellow-700/30';
          case 'English': return 'bg-purple-100 text-purple-800 ring-purple-800/20';
          case 'Elective': return 'bg-red-100 text-red-800 ring-red-700/20';
          default: return 'bg-gray-100 text-gray-700 ring-gray-600/20';
      }
  };

  return (
      <div className="min-h-screen flex flex-col items-center bg-neutral-900 px-4">
          <SearchBar onSearch={handleSearch} onFilter={handleFilter} />
          <div className="w-full max-w-4xl rounded-lg p-6 mb-6">
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {filteredCourses.map((course, courseIndex) => (
                      <div 
                          key={courseIndex} 
                          className="bg-neutral-800 p-4 rounded-lg hover:scale-105 transition-transform duration-200 cursor-pointer"
                          onClick={() => handleCourseClick(course)}
                      >
                          <h3 className="text-lg font-semibold gradient-text">{course.name} - {course.number}</h3>
                          <div className="flex flex-wrap gap-2 mt-2">
                              <span className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${getBadgeColor(course.department)}`}>
                                  {course.department}
                              </span>
                              {course.department !== 'Elective' && (
                                  <span className="inline-flex items-center rounded-md px-2 py-1 text-xs font-medium bg-orange-100 text-orange-800 ring-1 ring-inset ring-orange-800/20">
                                      Weighted
                                  </span>
                              )}
                          </div>
                      </div>
                  ))}
              </div>
          </div>
          {selectedCourse && (
              <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
                  <div className="bg-neutral-800 p-6 rounded-lg max-w-2xl w-full">
                      <h2 className="text-2xl font-bold mb-4 gradient-text">{selectedCourse.name}</h2>
                      <p className="text-neutral-300 mb-2">Number: {selectedCourse.number}</p>
                      <p className="text-neutral-300 mb-2">Credits: {selectedCourse.credits}</p>
                      <p className="text-neutral-300 mb-2">Department: {selectedCourse.department}</p>
                      <p className="text-neutral-300 mb-4">{selectedCourse.description}</p>
                      <button
                        onClick={() => setSelectedCourse(null)}
                        className="text-white bg-orange-700 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden"
                        >
                        <span className="relative z-10 transition-all duration-500 group-hover:pr-6">
                            CLOSE
                        </span>
                        <span className="absolute right-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:right-4">
                        &#10005;
                        </span>
                    </button>
                  </div>
              </div>
          )}
      </div>
  )
}

export default Courses;
