import React, { useState, useEffect } from "react";

export default function Build() {
  const [currentYear, setCurrentYear] = useState(1);
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    fetch('/freshman.json')
      .then(response => response.json())
      .then(data => setCourses(data));
  }, []);

  const handleYearChange = (direction) => {
    if (direction === 'prev' && currentYear > 1) {
      setCurrentYear(currentYear - 1);
    } else if (direction === 'next' && currentYear < 4) {
      setCurrentYear(currentYear + 1);
    }
  };

  const getYearName = (year) => {
    switch (year) {
      case 1:
        return "Freshman";
      case 2:
        return "Sophomore";
      case 3:
        return "Junior";
      case 4:
        return "Senior";
      default:
        return "";
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="w-full md:w-1/2 mb-8">
        <h1 className="gradient-text font-bold text-4xl mb-4">
          Let's build.
        </h1>
        <p className="text-white mb-8">
          Select courses you'd like to take in each year.
        </p>
        <div className="mt-8 flex justify-between mb-8">
          {currentYear > 1 && (
            <button
              className="text-white bg-orange-800 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden text-sm sm:text-base"
              onClick={() => handleYearChange('prev')}
            >
              <span className="relative z-10 transition-all duration-500 group-hover:pl-6">
                BACK
              </span>
              <span className="absolute left-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:left-4">
                ←
              </span>
            </button>
          )}
          <h2 className="gradient-text font-bold text-3xl">{getYearName(currentYear)}</h2>
          {currentYear < 4 && (
            <button
              className="text-white bg-orange-800 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden text-sm sm:text-base"
              onClick={() => handleYearChange('next')}
            >
              <span className="relative z-10 transition-all duration-500 group-hover:pr-6">
                NEXT
              </span>
              <span className="absolute right-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:right-4">
                →
              </span>
            </button>
          )}
        </div>
        <div className="grid grid-cols-1 gap-4">
          {[...Array(8)].map((_, index) => (
            <select key={index} className="w-full p-2 bg-neutral-700 text-white rounded-lg">
              <option value="">Select a course</option>
              {courses.map((course, courseIndex) => (
                <option key={courseIndex} value={course.id}>{course.name}</option>
              ))}
            </select>
          ))}
        </div>
      </div>
    </div>
  );
}
