import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import courseData from "../assets/data/other-three.json";
import OpenAI from 'openai';

const SearchBar = ({ onSearch, onFilter }) => {
  // ... (SearchBar component remains unchanged)
};

const Courses = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterOption, setFilterOption] = useState('all');
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [courseGeneralizations, setCourseGeneralizations] = useState({});
  const navigate = useNavigate();

  const openai = new OpenAI({
      apiKey: process.env.REACT_APP_OPENAI_API_KEY,
      dangerouslyAllowBrowser: true
  });

  useEffect(() => {
      const allCourses = courseData["sophomore-year"].flatMap(department => department.courses);
      let filtered = allCourses.filter(course =>
          course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          course.number.toLowerCase().includes(searchTerm.toLowerCase())
      );

      switch(filterOption) {
          case 'electives':
              filtered = filtered.filter(course => course.type === 'elective');
              break;
          case 'math':
              filtered = filtered.filter(course => course.type === 'math');
              break;
          case 'english':
              filtered = filtered.filter(course => course.type === 'english');
              break;
          case 'science':
              filtered = filtered.filter(course => course.type === 'science');
              break;
          case 'social-studies':
              filtered = filtered.filter(course => course.type === 'social-studies');
              break;
          case 'credits-low-high':
              filtered.sort((a, b) => a.credits - b.credits);
              break;
          case 'credits-high-low':
              filtered.sort((a, b) => b.credits - a.credits);
              break;
          default:
              break;
      }

      setFilteredCourses(filtered);

      // Generate course generalizations
      filtered.forEach(async (course) => {
          if (!courseGeneralizations[course.number]) {
              try {
                  const response = await openai.completions.create({
                      model: "text-davinci-002",
                      prompt: `Provide a brief generalization of the course "${course.name}" in the context of ${course.type} studies.`,
                      max_tokens: 100
                  });
                  setCourseGeneralizations(prev => ({
                      ...prev,
                      [course.number]: response.choices[0].text.trim()
                  }));
              } catch (error) {
                  console.error("Error generating course generalization:", error);
              }
          }
      });
  }, [searchTerm, filterOption]);

  const handleSearch = (term) => {
      setSearchTerm(term);
  };

  const handleFilter = (option) => {
      setFilterOption(option);
  };

  const handleCourseClick = (course) => {
      navigate(`/course/${course.number}`, { state: { course, generalization: courseGeneralizations[course.number] } });
  };

  return (
      <div className="min-h-screen flex flex-col items-center bg-neutral-900 px-4">
          <SearchBar onSearch={handleSearch} onFilter={handleFilter} />
          <div className="w-full max-w-4xl rounded-lg p-6 mb-6">
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {filteredCourses.map((course, courseIndex) => (
                      <div 
                          key={courseIndex} 
                          className="bg-neutral-800 p-4 rounded-lg hover:scale-105 transition-transform duration-200 cursor-pointer"
                          onClick={() => handleCourseClick(course)}
                      >
                          <h3 className="text-lg font-semibold gradient-text">{course.name}</h3>
                          <p className="text-neutral-300">Number: {course.number}</p>
                          <p className="text-neutral-300">Credits: {course.credits}</p>
                          <p className="text-neutral-300">Type: {course.type}</p>
                          <p className="text-neutral-300 mt-2">
                              {courseGeneralizations[course.number] || "Generating course overview..."}
                          </p>
                      </div>
                  ))}
              </div>
          </div>
      </div>
  )
}

export default Courses;
