import React, { useState, useEffect, useCallback, useMemo } from "react";
import PropTypes from 'prop-types';
import otherData from "../assets/data/other-three.json";
import freshmanData from "../assets/data/freshman.json";

// ... (previous code remains unchanged)

export default function Build({ courses: initialCourses }) {
  const [currentYear, setCurrentYear] = useState(1);
  const [courses, setCourses] = useState(initialCourses);
  const [allSelectedCourses, setAllSelectedCourses] = useState({ 1: Array(8).fill(null), 2: Array(8).fill(null), 3: Array(8).fill(null), 4: Array(8).fill(null) });
  const [searchTerms, setSearchTerms] = useState(Array(8).fill(''));
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [doubleBlockedCourses, setDoubleBlockedCourses] = useState({ 1: 0, 2: 0, 3: 0, 4: 0 });
  const [aiSuggestions, setAiSuggestions] = useState([]);
  const [userPreferences, setUserPreferences] = useState({});

  useEffect(() => {
    const yearData = { 1: freshmanData["freshman-year"], 2: otherData.departments, 3: otherData.departments, 4: otherData.departments };
    setCourses(yearData[currentYear]);
  }, [currentYear]);

  useEffect(() => {
    // Simulating AI asking questions and providing suggestions
    const askAIQuestions = async () => {
      // In a real implementation, this would be an API call to an AI service
      const questions = [
        "What subjects do you enjoy the most?",
        "Are you interested in any specific career paths?",
        "Do you prefer hands-on or theoretical learning?",
        "Are you interested in taking any advanced or AP courses?",
      ];

      const userAnswers = {};
      for (let question of questions) {
        // In a real implementation, this would be a user interface for input
        const answer = prompt(question);
        userAnswers[question] = answer;
      }

      setUserPreferences(userAnswers);

      // Simulating AI suggestions based on user answers
      const suggestions = generateAISuggestions(userAnswers);
      setAiSuggestions(suggestions);
    };

    askAIQuestions();
  }, []);

  const generateAISuggestions = (preferences) => {
    // This is a simplified version. In a real implementation, this would be more sophisticated
    const suggestions = [];

    if (preferences["What subjects do you enjoy the most?"].toLowerCase().includes("math")) {
      suggestions.push("Consider taking advanced math courses like Pre-Calculus AP");
    }

    if (preferences["Are you interested in taking any advanced or AP courses?"].toLowerCase().includes("yes")) {
      suggestions.push("Look into AP courses like Human Geography AP or Biology Advanced");
    }

    if (preferences["Do you prefer hands-on or theoretical learning?"].toLowerCase().includes("hands-on")) {
      suggestions.push("You might enjoy courses like Digital Media or Introduction to Engineering Design");
    }

    return suggestions;
  };

  // ... (rest of the code remains unchanged)

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="w-full max-w-3xl mb-8">
        <h1 className="gradient-text font-bold text-5xl mb-6 text-center">Let's build.</h1>
        <p className="text-white text-lg mb-8 text-center">Select your classes for the next four years!</p>

        {aiSuggestions.length > 0 && (
          <div className="mb-8 p-4 bg-neutral-800 rounded-lg">
            <h3 className="gradient-text text-xl mb-4 font-semibold">AI Suggestions:</h3>
            <ul className="list-disc pl-5 text-white">
              {aiSuggestions.map((suggestion, index) => (
                <li key={index}>{suggestion}</li>
              ))}
            </ul>
          </div>
        )}

        <div className="mt-8 flex justify-center items-center mb-12">
          <h2 className="gradient-text font-bold text-4xl">{getYearName(currentYear)}</h2>
        </div>

        {/* ... (rest of the JSX remains unchanged) */}
      </div>
    </div>
  );
}

Build.propTypes = {
  courses: PropTypes.array.isRequired,
};
