import React, { useState, useCallback, useMemo, useEffect } from "react";
import PropTypes from "prop-types";
import courseListData from "../assets/data/course-list.json";
import CourseDropdown from "./CourseDropdown";

CourseDropdown.propTypes = {
  index: PropTypes.number.isRequired,
  subject: PropTypes.string.isRequired,
  courses: PropTypes.array.isRequired,
  searchTerm: PropTypes.string.isRequired,
  onSearchChange: PropTypes.func.isRequired,
  onCourseSelect: PropTypes.func.isRequired,
  selectedCourse: PropTypes.object,
  isActive: PropTypes.bool.isRequired,
  onToggle: PropTypes.func.isRequired,
  disabled: PropTypes.bool,
};

export default function Build({ courses: initialCourses }) {
  const [state, setState] = useState({
    currentYear: 1,
    courses: initialCourses,
    selectedCourses: Object.fromEntries([1, 2, 3, 4].map(year => [year, Array(16).fill(null)])),
    searchTerms: Array(16).fill(""),
    activeDropdown: null,
    doubleBlocked: Object.fromEntries([1, 2, 3, 4].map(year => [year, 0])),
    excludedSubjects: Object.fromEntries([1, 2, 3, 4].map(year => [year, []])),
    hasOnlineCourses: Object.fromEntries([1, 2, 3, 4].map(year => [year, false])),
    onlineCourses: Object.fromEntries([1, 2, 3, 4].map(year => [year, null])),
    isReviewing: false,
    showOnlinePrompt: true,
    onlineSubject: null,
  });
  const { currentYear, courses, selectedCourses, searchTerms, activeDropdown, doubleBlocked, excludedSubjects, hasOnlineCourses, onlineCourses, isReviewing, showOnlinePrompt, onlineSubject } = state;

  useEffect(() => { setState(prev => ({ ...prev, courses: courseListData })); }, []);

  const getYearName = year => ["", "Freshman", "Sophomore", "Junior", "Senior"][year];
  const handleSearchChange = useCallback((index, value) => setState(prev => ({ ...prev, searchTerms: { ...prev.searchTerms, [index]: value } })), []);
  const isHalfSemesterCourse = course => course?.key?.match(/0\.5(?!PE)/);
  const handleYearChange = dir => setState(prev => ({ ...prev, currentYear: Math.max(1, Math.min(4, prev.currentYear + (dir === "prev" ? -1 : 1))), searchTerms: Array(16).fill(""), activeDropdown: null }));

  const handleCourseSelect = (index, course) => {
    const yearCourses = [...selectedCourses[currentYear]];
    const oldCourse = yearCourses[index];
    const isDB = course?.name?.includes("DB");
    const dbCount = isDB ? doubleBlocked[currentYear] + 1 : (oldCourse?.name?.includes("DB") ? doubleBlocked[currentYear] - 1 : doubleBlocked[currentYear]);
    yearCourses[index] = course;

    setState(prev => ({
      ...prev,
      selectedCourses: { ...selectedCourses, [currentYear]: yearCourses },
      doubleBlocked: { ...doubleBlocked, [currentYear]: dbCount },
      activeDropdown: null,
      excludedSubjects: !["math", "english", "social", "science"].includes(course.name?.split(" ")[0].toLowerCase())
        ? { ...excludedSubjects, [currentYear]: [...new Set([...excludedSubjects[currentYear], course.name.split(" ")[0].toLowerCase()])] }
        : excludedSubjects,
      searchTerms: { ...searchTerms, [index]: "" },
    }));
  };

  const getAllCourses = useMemo(() => courses ? Object.values(courses).flat() : [], [courses]);
  const filteredCourses = useCallback((index, subject, isSecondSem) => {
    const availableCourses = courses[subject.toLowerCase()] || [];
    return availableCourses.filter(course => {
      const isAllowed = !excludedSubjects[currentYear].includes(course.name.split(" ")[0].toLowerCase()) || allSelectedCourses[currentYear][index]?.name.startsWith(course.name.split(" ")[0]);
      return isAllowed && (!isSecondSem || isHalfSemesterCourse(course));
    });
  }, [courses, excludedSubjects, selectedCourses, currentYear]);

  const renderDropdown = (index, subject) => (
    <div className={`flex flex-col ${doubleBlocked[currentYear] >= index ? "opacity-50 bg-neutral-900" : ""}`}>
      <CourseDropdown
        key={index} index={index} subject={onlineCourses[currentYear] === subject.toLowerCase() ? "Select Any Course" : subject}
        courses={onlineCourses[currentYear] === subject.toLowerCase() ? getAllCourses : (subject === "All Courses" ? getAllCourses : filteredCourses(index, subject))}
        searchTerm={searchTerms[index]} onSearchChange={handleSearchChange} onCourseSelect={handleCourseSelect}
        selectedCourse={selectedCourses[currentYear][index]} isActive={activeDropdown === index}
        onToggle={() => setState(prev => ({ ...prev, activeDropdown: activeDropdown === index ? null : index }))}
        disabled={doubleBlocked[currentYear] >= index}
      />
      {isHalfSemesterCourse(selectedCourses[currentYear][index]) && (
        <div className="mt-2 ml-4 border-l-2 border-orange-500 pl-4">
          <CourseDropdown
            key={index + 8} index={index + 8} subject={subject}
            courses={subject === "All Courses" ? getAllCourses : filteredCourses(index + 8, subject, true)}
            searchTerm={searchTerms[index + 8]} onSearchChange={handleSearchChange} onCourseSelect={handleCourseSelect}
            selectedCourse={selectedCourses[currentYear][index + 8]} isActive={activeDropdown === index + 8}
            onToggle={() => setState(prev => ({ ...prev, activeDropdown: activeDropdown === index + 8 ? null : index + 8 }))}
            disabled={doubleBlocked[currentYear] >= index}
          />
        </div>
      )}
    </div>
  );

  if (showOnlinePrompt) return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="w-full max-w-3xl bg-neutral-800 rounded-lg shadow-xl p-8">
        <h2 className="gradient-text font-bold text-3xl mb-6 text-center">{getYearName(currentYear)} Year - Online Courses</h2>
        <p className="text-white text-lg mb-6 text-center">Have you taken any online courses for this year?</p>
        <div className="flex justify-center gap-4 mb-6">
          <button onClick={() => setState(prev => ({ ...prev, hasOnlineCourses: { ...hasOnlineCourses, [currentYear]: true } }))} className="text-white bg-orange-700 px-6 py-3 rounded-lg hover:bg-orange-600">Yes</button>
          <button onClick={() => setState(prev => ({ ...prev, showOnlinePrompt: false }))} className="text-white bg-neutral-600 px-6 py-3 rounded-lg hover:bg-neutral-500">No</button>
        </div>
        {hasOnlineCourses[currentYear] && (
          <div className="mt-6">
            <p className="text-white text-lg mb-4 text-center">Select the subject of your online course:</p>
            <div className="flex justify-center gap-4 flex-wrap">
              {["Math", "Science", "English", "Social Studies"].map(subject => (
                <button
                  key={subject}
                  onClick={() => setState(prev => ({
                    ...prev, onlineSubject: subject.toLowerCase(), showOnlinePrompt: false
                  }))}
                  className="text-white bg-orange-700 px-4 py-2 rounded-lg hover:bg-orange-600"
                >
                  {subject}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="w-full max-w-3xl mb-8">
        <h1 className="gradient-text font-bold text-5xl mb-6 text-center">Let's build.</h1>
        <p className="text-white text-lg mb-8 text-center">Select your classes for the next four years!</p>
        <div className="mt-8 flex justify-center items-center mb-12">
          <h2 className="gradient-text font-bold text-4xl">{getYearName(currentYear)}</h2>
        </div>
        <h3 className="gradient-text text-xl mb-4 text-center font-semibold">Core Classes</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          {["Math", "English", "Social Studies", "Science"].map((subject, index) => renderDropdown(index, subject))}
        </div>
        <h3 className="gradient-text text-xl mb-4 text-center font-semibold">Electives</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          {["PE", "World Language", "All Courses"].map((subject, index) => renderDropdown(index + 4, subject))}
        </div>
        <div className="flex justify-between mt-12">
          <button onClick={() => handleYearChange("prev")} className="text-white bg-orange-700 px-6 py-3 rounded-lg hover:bg-orange-600" disabled={currentYear === 1}>Previous Year</button>
          <button onClick={() => setState(prev => ({ ...prev, isReviewing: !isReviewing }))} className="text-white bg-orange-700 px-6 py-3 rounded-lg hover:bg-orange-600">Review Selections</button>
          <button onClick={() => handleYearChange("next")} className="text-white bg-orange-700 px-6 py-3 rounded-lg hover:bg-orange-600" disabled={currentYear === 4}>Next Year</button>
        </div>
      </div>
    </div>
  );
}
