import { AnimatePresence } from "framer-motion";
import { motion } from "framer-motion";
import { useState } from "react";

export default function IntroText() {

  const [showIntro, setShowIntro] = useState(true);




  const handleNext = () => {
      setShowIntro(false);
  };




  const handleBack = () => {
      setShowIntro(true);
  };





  const fadeIn = {
      hidden: { opacity: 0 },
      visible: { opacity: 1, transition: { duration: 0.5 } }
  };

  return (





















































      <AnimatePresence mode="wait">
          {showIntro ? (
              <motion.div
                  key="intro"
                  id="intro"
                  className="transition-opacity duration-500 ease-in-out"
                  initial="hidden"
                  animate="visible"
                  exit="hidden"
                  variants={fadeIn}
              >
                  <motion.h1
                      className="gradient-text font-bold text-4xl"
                      variants={fadeIn}
                  >
                      Hey there, RRISD student!
                  </motion.h1>
                  <motion.p
                      className="text-white min-w-60 w-5/12 mb-5"
                      variants={fadeIn}
                  >
                      Congratulations, you're about to be a high schooler! Before you
                      transition, it is vital to understand what your passion is and how
                      you will move forward with your career. We're going to do something
                      called a <b>four year plan</b>.
                  </motion.p>
                  <motion.button
                      onClick={handleNext}
                      className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
                      variants={fadeIn}
                  >
                      NEXT
                  </motion.button>
              </motion.div>
          ) : (
              <motion.div
                  key="nextContent"
                  id="nextContent"
                  className="transition-opacity duration-500 ease-in-out"
                  initial="hidden"
                  animate="visible"
                  exit="hidden"
                  variants={fadeIn}
              >
                  <motion.h2
                      className="gradient-text font-bold text-4xl"
                      variants={fadeIn}
                  >
                      What is a four year plan?
                  </motion.h2>
                  <motion.p
                      className="text-white min-w-60 w-5/12 mb-5"
                      variants={fadeIn}
                  >
                      Here you can add more content about the four year plan or any other
                      information you want to show after clicking 'NEXT'.
                  </motion.p>
                  <motion.button
                      onClick={handleBack}
                      className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
                      variants={fadeIn}
                  >
                      BACK
                  </motion.button>
              </motion.div>
          )}
      </AnimatePresence>
  );

}
