import React, { useState } from 'react';
import Schools from "../assets/data/schools.json";

export default function Welcome() {
    const [selectedSchool, setSelectedSchool] = useState(null);

    const handleSchoolClick = (index) => {
        setSelectedSchool(index);
    };

    const handleSubmit = () => {
        console.log("Selected school:", Schools[selectedSchool]);
    };

    return (
        <div className="flex items-center min-h-screen">
            <div id="welcome" className="mx-10 flex flex-col md:flex-row justify-between w-full">
                <div className="w-full md:w-1/2" id="intro">
                    <h1 className="gradient-text font-bold text-4xl">Hey there, RRISD student!</h1>
                    <p className="text-white">Congratulations, you're about to be a high schooler! Before you transition, it is vital to understand what your passion is and how you will move forward with our career. We're going to do something called a <b>four year plan</b>.</p>
                </div>
                <div className="w-full md:w-1/2 mt-6 md:mt-0">
                    <div className="bg-neutral-800 text-white p-4 rounded-lg">
                        <h2 className="text-xl mb-4">Select your school:</h2>
                        <ul>
                            {Schools.map((school, index) => (
                                <li
                                    key={index}
                                    className={`cursor-pointer p-2 rounded-lg transition-all duration-300 ${selectedSchool === index ? 'gradient-text' : 'hover:bg-neutral-700'}`}
                                    onClick={() => handleSchoolClick(index)}
                                >
                                    {school.name}
                                </li>
                            ))}
                        </ul>
                        {selectedSchool !== null && (
                            <button
                                className="mt-4 bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-all duration-300"
                                onClick={handleSubmit}
                            >
                                Submit
                            </button>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}
