          if (row[1]?.includes("Principles of Robotics")) {
            foundRobotics = true;
          }
          if (row[1]?.includes("Digital Media")) {
            foundDigitalMedia = true;
          }
          if (row[1]?.includes("Principles of Business, Marketing & Finance")) {
            foundPrinciplesBusiness = true;
          }
          if (row[1]?.includes("Debate I")) {
            foundDebateI = true;
          }
          if (row[1]?.includes("Team Sports")) {
            foundTeamSports = true;
          }
          if (row[1]?.includes("Sports Medicine I")) {
            foundSportsMedicine = true;
          }
          if (row[1]?.includes("Baseball/SH")) {
            foundBaseball = true;
