            currentDepartment = "Social Studies";
          } else if (foundAlgebraI && !foundPrinciplesDance) {
            currentDepartment = "Mathematics";
          } else if (foundPrinciplesDance && !foundArtI) {
            currentDepartment = "Dance";
          } else if (foundArtI && !foundAeroSpace) {
            currentDepartment = "Art";
          } else if (foundAeroSpace && !foundComputerScience) {
            currentDepartment = "JROTC";
          } else if (foundComputerScience && !foundEngineering) {
            currentDepartment = "Computer Science";
          } else if (foundEngineering && !foundRobotics) {
            currentDepartment = "Engineering";
          } else if (foundRobotics && !foundDigitalMedia) {
            currentDepartment = "Robotics";
          } else if (foundDigitalMedia && !foundPrinciplesBusiness) {
            currentDepartment = "Animation";
          } else if (foundPrinciplesBusiness && !foundDebateI) {
            currentDepartment = "Business Management & Administration";
          } else if (foundDebateI && !foundTeamSports) {
            currentDepartment = "Debate";
          } else if (foundTeamSports && !foundSportsMedicine) {
            currentDepartment = "Physical Education";
          } else if (foundSportsMedicine && !foundBaseball) {
            currentDepartment = "Misc. Courses";
          } else if (foundBaseball) {
            currentDepartment = "UIL Athletics";
          } else {
            for (let i = index; i >= 0; i--) {
              if (array[i][0] && array[i][0].toUpperCase() === array[i][0]) {
                currentDepartment = departmentMap[array[i][0]] || array[i][0];
                break;
              }
            }
          }

          const createCourse = (nameIndex, numberIndex, creditsIndex) => {
            const courseName = row[nameIndex] || "";
            const courseType = [];

            if (courseName.includes("AP")) courseType.push("AP");
            if (courseName.includes("IB")) courseType.push("IB");
            if (courseName.includes("Honors")) courseType.push("Honors");
            if (courseName.includes("Pre-AP")) courseType.push("Pre-AP");
            if (courseName.includes("Dual")) courseType.push("Dual Credit");
            if (courseName.includes("Offblock")) courseType.push("Offblock");

            return {
              name: courseName,
              department: currentDepartment,
              number: row[numberIndex] || "Unknown",
              numberBadge: row[numberIndex] ? `Course #${row[numberIndex]}` : "Unknown Number",
              information: courseLinks[courseName] || "Unknown",
              credits: row[creditsIndex]
                ? row[creditsIndex]
                    .split(",")
                    .map((key) => creditMeanings[key.trim()] || key.trim())
                    .join(", ")
                : "Single Credit Course",
              courseType: courseType,
              link: currentDepartment,
            };
          };

          if (
            row[1] &&
            !row[1].toLowerCase().includes("course name") &&
            !row[1].toLowerCase().includes("course") &&
            !row[1].toLowerCase().includes("half credit course") &&
            !row[1].toLowerCase().includes("two credit course") &&
            !row[1].toLowerCase().includes("three credit course") &&
            !row[1]
              .toLowerCase()
              .includes("austin community college dual credit course") &&
            !row[1]
              .toLowerCase()
              .includes("university of texas dual credit course")
          )
            acc.push(createCourse(1, 0, 2));
          return acc;
        }, []);

        let foundFrenchI = false;
        letfoundAlgebraI = false;
        foundPrinciplesDance = false;
        let foundDanceTeamIV = false;
        courses = response.data.values.reduce((acc, row, index, array) => {
          if (!row[0] || !row[1]) return acc;

          let currentDepartment = "";
          if (row[5]?.includes("French I")) {
            foundFrenchI = true;
          }
          if (row[5]?.includes("Algebra I")) {
            foundAlgebraI = true;
          }
          if (row[5]?.includes("Principles of Dance I")) {
            foundPrinciplesDance = true;
          }
          if (row[5]?.includes("Dance Performance Ensemble IV: Dance Team")) {
            foundDanceTeamIV = true;
          }

          if (!foundWorldGeography) {
            currentDepartment = "English Language Arts";
          } else if (foundWorldGeography && !foundAlgebraI) {
            currentDepartment = "Social Studies";
          } else if (foundAlgebraI && !foundPrinciplesDance) {
            currentDepartment = "Mathematics";
          } else if (foundPrinciplesDance && !foundDanceTeamIV) {
            currentDepartment = "Dance";
          } else {
            for (let i = index; i >= 0; i--) {
              if (array[i][0] && array[i][0].toUpperCase() === array[i][0]) {
                currentDepartment = departmentMap[array[i][0]] || array[i][0];
                break;
              }
            }
          }

          const createCourse = (nameIndex, numberIndex, creditsIndex) => {
            const courseName = row[nameIndex] || "";
            const courseType = [];

            if (courseName.includes("AP")) courseType.push("AP");
            if (courseName.includes("IB")) courseType.push("IB");
            if (courseName.includes("Honors")) courseType.push("Honors");
            if (courseName.includes("Pre-AP")) courseType.push("Pre-AP");
            if (courseName.includes("Dual")) courseType.push("Dual Credit");
            if (courseName.includes("Offblock")) courseType.push("Offblock");

            return {
              name: courseName,
              department: currentDepartment,
              number: row[numberIndex] || "Unknown",
              numberBadge: row[numberIndex] ? `Course #${row[numberIndex]}` : "Unknown Number",
              information: courseLinks[courseName] || "Unknown",
              credits: row[creditsIndex]
                ? row[creditsIndex]
                    .split(",")
                    .map((key) => creditMeanings[key.trim()] || key.trim())
                    .join(", ")
                : "Single Credit Course",
              courseType: courseType,
              link: currentDepartment,
            };
          };

          if (
            row[5]?.trim() &&
            !row[5].toLowerCase().includes("course name") &&
            !row[5].toLowerCase().includes("course") &&
            !row[5].toLowerCase().includes("half credit course") &&
            !row[5].toLowerCase().includes("two credit course") &&
            !row[5].toLowerCase().includes("three credit course") &&
            !row[5]
              .toLowerCase()
              .includes("austin community college dual credit course") &&
            !row[5]
              .toLowerCase()
              .includes("university of texas dual credit course")
          )
            acc.push(createCourse(5, 4, 6));
          return acc;
        }, courses);

        setAllCourses(courses);
      } catch (error) {
        console.error("Error fetching courses:", error);
      }
    };
    fetchCourses();
  }, []);

  useEffect(() => {
    let filtered = allCourses.filter((course) =>
      course.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    if (filterOption !== "all") {
      if (
        ["AP", "IB", "Honors", "Pre-AP", "Dual Credit"].includes(filterOption)
      ) {
        filtered = filtered.filter((course) =>
          course.courseType.includes(filterOption)
        );
      } else {
        filtered = filtered.filter(
          (course) => course.department === filterOption
        );
      }
    }
    setFilteredCourses(filtered);
  }, [searchTerm, filterOption, allCourses]);

  const handleCourseClick = (course) => {
    setSelectedCourse(course);
    toast("Scroll down to see all course information!", {
      position: "top-center",
      autoClose: 3000,
      theme: "dark",
      style: {
        background: "#262626",
        color: "#fff",
        borderRadius: "8px",
        boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
        fontSize: "14px",
      },
      progressStyle: { background: "#FF8C00" },
    });
  };

  const badgeColors = {
    "English Language Arts": "bg-purple-100 text-purple-800 ring-purple-800/20",
    Mathematics: "bg-blue-100 text-blue-800 ring-blue-800/20",
    Science: "bg-green-100 text-green-800 ring-green-700/30",
    "Social Studies": "bg-yellow-100 text-yellow-900 ring-yellow-700/30",
    "Languages Other Than English":
      "bg-pink-100 text-pink-800 ring-pink-800/20",
    "Fine Arts": "bg-indigo-100 text-indigo-800 ring-indigo-800/20",
    "Physical Education": "bg-orange-100 text-orange-800 ring-orange-800/20",
    "Career and Technical Education": "bg-red-100 text-red-800 ring-red-700/20",
    "Other Electives": "bg-gray-100 text-gray-800 ring-gray-800/20",
    AP: "bg-emerald-100 text-emerald-800 ring-emerald-800/20",
    IB: "bg-cyan-100 text-cyan-800 ring-cyan-800/20",
    "Dual Credit": "bg-violet-100 text-violet-800 ring-violet-800/20",
    "Offblock": "bg-violet-100 text-violet-800 ring-violet-800/20",
