import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { MagnifyingGlassIcon, ChevronDownIcon, XMarkIcon } from '@heroicons/react/24/outline';

const SearchBar = ({ onSearch, onFilter }) => {
      const SearchBar = ({ onSearch, onFilter }) => {
    const [searchTerm, setSearchTerm] = useState('')
    const [filterOpen, setFilterOpen] = useState(false)
    const [selectedFilters, setSelectedFilters] = useState([])

    const handleSearch = (e) => {
      e.preventDefault()
      onSearch(searchTerm)
    }

    const handleFilterToggle = () => {
      setFilterOpen(!filterOpen)
    }

    const handleFilterSelect = (filter) => {
      if (selectedFilters.includes(filter)) {
        setSelectedFilters(selectedFilters.filter(f => f !== filter))
      } else {
        setSelectedFilters([...selectedFilters, filter])
      }
    }

    const applyFilters = () => {
      onFilter(selectedFilters)
      setFilterOpen(false)
    }

    return (
      <div className="relative mb-4">
        <form onSubmit={handleSearch} className="flex items-center">
          <input
            type="text"
            placeholder="Search courses..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full px-4 py-2 border rounded-l-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            type="submit"
            className="px-4 py-2 bg-blue-500 text-white rounded-r-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <MagnifyingGlassIcon className="h-5 w-5" />
          </button>
          <div className="relative ml-2">
            <button
              type="button"
              onClick={handleFilterToggle}
              className="px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 focus:outline-none focus:ring-2 focus:ring-gray-500 flex items-center"
            >
              Filter
              <ChevronDownIcon className="h-4 w-4 ml-1" />
            </button>
            {filterOpen && (
              <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg z-10">
                <div className="py-1">
                  {['Beginner', 'Intermediate', 'Advanced'].map((filter) => (
                    <label key={filter} className="flex items-center px-4 py-2 hover:bg-gray-100">
                      <input
                        type="checkbox"
                        checked={selectedFilters.includes(filter)}
                        onChange={() => handleFilterSelect(filter)}
                        className="mr-2"
                      />
                      {filter}
                    </label>
                  ))}
                </div>
                <div className="border-t px-4 py-2">
                  <button
                    onClick={applyFilters}
                    className="w-full px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    Apply Filters
                  </button>
                </div>
              </div>
            )}
          </div>
        </form>
        {selectedFilters.length > 0 && (
          <div className="mt-2 flex flex-wrap">
            {selectedFilters.map((filter) => (
              <span key={filter} className="bg-blue-100 text-blue-800 text-sm font-medium mr-2 px-2.5 py-0.5 rounded flex items-center">
                {filter}
                <XMarkIcon
                  className="h-4 w-4 ml-1 cursor-pointer"
                  onClick={() => handleFilterSelect(filter)}
                />
              </span>
            ))}
          </div>
        )}
      </div>
    )
}
};

const Courses = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [filterOption, setFilterOption] = useState('all');
    const [filteredCourses, setFilteredCourses] = useState([]);
    const [selectedCourse, setSelectedCourse] = useState(null);
    const [courses, setCourses] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
      const fetchCourses = async () => {
        try {
          const response = await fetch('https://api.example.com/courses');
          const data = await response.json();
          setCourses(data);
        } catch (error) {
          console.error('Error fetching courses:', error);
          toast.error('Failed to fetch courses. Please try again later.');
        }
      };

      fetchCourses();
    }, []);

    useEffect(() => {
      let filtered = courses.filter(course =>
        course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.number.toLowerCase().includes(searchTerm.toLowerCase())
      );

      if (filterOption !== 'all' && filterOption !== 'credits-low-high' && filterOption !== 'credits-high-low') {
        filtered = filtered.filter(course => course.department === filterOption);
      }

      if (filterOption === 'credits-low-high') {
        filtered.sort((a, b) => a.credits - b.credits);
      } else if (filterOption === 'credits-high-low') {
        filtered.sort((a, b) => b.credits - a.credits);
      }

      setFilteredCourses(filtered);
    }, [searchTerm, filterOption, courses]);

    const handleSearch = (term) => {
      setSearchTerm(term);
    };

    const handleFilter = (option) => {
      setFilterOption(option);
    };

    const handleCourseClick = (course) => {
      setSelectedCourse(course);
      toast("Scroll down to see all course information!", {
        position: "top-center",
        autoClose: 3000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
        style: {
          background: '#262626',
          color: '#fff',
          borderRadius: '8px',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
          fontSize: '14px',
        },
        progressStyle: {
          background: '#FF8C00',
        },
      });
    };

    const getBadgeColor = (department) => {
      switch(department) {
        case 'Math': return 'bg-blue-100 text-blue-800 ring-blue-800/20';
        case 'Science': return 'bg-green-100 text-green-800 ring-green-700/30';
        case 'Social Studies': return 'bg-yellow-100 text-yellow-900 ring-yellow-700/30';
        case 'English': return 'bg-purple-100 text-purple-800 ring-purple-800/20';
        case 'Elective': return 'bg-red-100 text-red-800 ring-red-700/20';
        default: return 'bg-gray-100 text-gray-700 ring-gray-600/20';
      }
    };

    return (
      <div className="min-h-screen flex flex-col items-center bg-neutral-900 px-4">
        <SearchBar onSearch={handleSearch} onFilter={handleFilter} />
        <div className="w-full max-w-4xl rounded-lg p-6 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {filteredCourses.map((course, courseIndex) => (
              <div 
                key={courseIndex} 
                className="bg-neutral-800 p-4 rounded-lg hover:scale-105 transition-transform duration-200 cursor-pointer"
                onClick={() => handleCourseClick(course)}
              >
                <h3 className="text-lg font-semibold gradient-text">{course.name} - {course.number}</h3>
                <div className="flex flex-wrap gap-2 mt-2">
                  <span className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${getBadgeColor(course.department)}`}>
                    {course.department}
                  </span>
                  {course.department !== 'Electives' && (
                    <span className="inline-flex items-center rounded-md px-2 py-1 text-xs font-medium bg-orange-100 text-orange-800 ring-1 ring-inset ring-orange-800/20">
                      Weighted
                    </span>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
        {selectedCourse && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 overflow-y-auto">
            <div className="bg-neutral-800 p-8 rounded-lg max-w-2xl w-full shadow-2xl transform transition-all duration-300 ease-in-out my-8">
              <h2 className="text-3xl font-bold mb-6 gradient-text">{selectedCourse.name}</h2>
              <div className="space-y-4 text-neutral-300 max-h-[60vh] overflow-y-auto pr-4">
                <p><span className="font-semibold">Number:</span> {selectedCourse.number || 'N/A'}</p>
                <p><span className="font-semibold">Credits:</span> {selectedCourse.credits || 'N/A'}</p>
                <p><span className="font-semibold">Length:</span> {selectedCourse.length || 'N/A'}</p>
                <p><span className="font-semibold">Grade:</span> {selectedCourse.grade || 'N/A'}</p>
                <p><span className="font-semibold">Prerequisites:</span> {selectedCourse.prerequisites || 'N/A'}</p>
                <p><span className="font-semibold">Description:</span> {selectedCourse.description || 'N/A'}</p>
                <p className="font-semibold mb-2">Grading:</p>
                {selectedCourse.grading && Object.entries(JSON.parse(selectedCourse.grading)).length > 0 ? (
                  <ul className="list-disc pl-5 space-y-1">
                    {Object.entries(JSON.parse(selectedCourse.grading)).map(([key, value], index) => (
                      <li key={index}>
                        <span className="font-medium">{key.replace(/([A-Z])/g, ' $1').trim().charAt(0).toUpperCase() + key.replace(/([A-Z])/g, ' $1').trim().slice(1)}:</span> {value}%
                      </li>
                    ))}
                  </ul>
                ) : (
                  <li>N/A</li>
                )}
                <p><span className="font-semibold">Instructional Style:</span> {selectedCourse.instructionalstyle || 'N/A'}</p>
                <p><span className="font-semibold">Homework Time:</span> {selectedCourse.homeworktime || 'N/A'}</p>
                <p><span className="font-semibold">Summer Requirements:</span> {selectedCourse.summerrequirements || 'N/A'}</p>
                <p><span className="font-semibold">Summer Enrichment:</span></p>
                <ul className="list-disc list-inside pl-4">
                  {selectedCourse.summerenrichment && JSON.parse(selectedCourse.summerenrichment).length > 0 ? 
                    JSON.parse(selectedCourse.summerenrichment).map((link, index) => (
                      <li key={index}><a href={link} target="_blank" rel="noopener noreferrer" className="text-blue-400 hover:underline">{`Resource ${index + 1}`}</a></li>
                    ))
                    : <li>N/A</li>
                  }
                </ul>
              </div>
              <div className="mt-8 flex justify-end">
                <button
                  onClick={() => setSelectedCourse(null)}
                  className="text-white bg-orange-700 p-3 rounded-md hover:rotate-[15deg] hover:bg-orange-600 transition-all duration-300 group inline-flex items-center justify-center relative overflow-hidden"
                >
                  <span className="relative z-10 transition-all duration-500 transform">
                    <XMarkIcon className="h-6 w-6" />
                  </span>
                </button>
              </div>
            </div>
          </div>
        )}
        <ToastContainer />
      </div>
    )
}

export default Courses;
