import React, { useState, useCallback, useMemo, useEffect } from "react";
import PropTypes from 'prop-types';
import axios from "axios";
import CourseDropdown from "./CourseDropdown";

CourseDropdown.propTypes = {
  index: PropTypes.number.isRequired,
  subject: PropTypes.string.isRequired,
  courses: PropTypes.array.isRequired,
  searchTerm: PropTypes.string.isRequired,
  onSearchChange: PropTypes.func.isRequired,
  onCourseSelect: PropTypes.func.isRequired,
  selectedCourse: PropTypes.object,
  isActive: PropTypes.bool.isRequired,
  onToggle: PropTypes.func.isRequired,
};

export default function Build() {
  const [currentYear, setCurrentYear] = useState(1);
  const [courses, setCourses] = useState({});
  const [allSelectedCourses, setAllSelectedCourses] = useState({ 1: Array(8).fill(null), 2: Array(8).fill(null), 3: Array(8).fill(null), 4: Array(8).fill(null) });
  const [searchTerms, setSearchTerms] = useState(Array(8).fill(''));
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [doubleBlockedCourses, setDoubleBlockedCourses] = useState({ 1: 0, 2: 0, 3: 0, 4: 0 });
  const [isReviewing, setIsReviewing] = useState(false);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await axios.get(
          `https://sheets.googleapis.com/v4/spreadsheets/19XGBFiAJC3L4CKwxeMaAs3itA7AFeQNoNQbeJAiXZ8U/values/Sheet1!A4:Z500?key=AIzaSyCxATN22mSeipVReXFJDsTleeGGkx8nDTg`
        );

        let foundWorldGeography = false;
        let foundAlgebraI = false;
        let foundEnglishI = false;

        const processedCourses = {
          math: [],
          english: [],
          "social studies": [],
          science: [],
          elective: []
        };

        response.data.values.forEach((row) => {
          if (!row[0] || !row[1]) return;

          if (row[1]?.includes("English I")) {
            foundEnglishI = true;
          }
          if (row[1]?.includes("World Geography")) {
            foundWorldGeography = true;
          }
          if (row[1]?.includes("Algebra I")) {
            foundAlgebraI = true;
          }

          let currentDepartment = "";
          if (foundEnglishI && !foundWorldGeography) {
            currentDepartment = "English Language Arts";
          } else if (foundWorldGeography && !foundAlgebraI) {
            currentDepartment = "Social Studies";
          } else if (foundAlgebraI) {
            currentDepartment = "Mathematics";
          }

          if (row[1] && !row[1].toLowerCase().includes("course name")) {
            const course = {
              name: row[1],
              number: row[0] || "Unknown",
              key: row[2] ? row[2].split(",").map(k => k.trim()) : []
            };

            if (currentDepartment) {
              processedCourses[currentDepartment].push(course);
            }
          }
        });

        setCourses(processedCourses);
      } catch (error) {
        console.error("Error fetching courses:", error);
      }
    };
    fetchCourses();
  }, []);

  const handleYearChange = (direction) => {
    if (direction === 'prev' && currentYear > 1) setCurrentYear(currentYear - 1);
    else if (direction === 'next' && currentYear < 4) setCurrentYear(currentYear + 1);
    setSearchTerms(Array(8).fill(''));
    setActiveDropdown(null);
  };

  const getYearName = (year) => ["", "Freshman", "Sophomore", "Junior", "Senior"][year];

  const handleSearchChange = useCallback((index, value) => {
    setSearchTerms(prev => { const newTerms = [...prev]; newTerms[index] = value; return newTerms; });
  }, []);

  const handleCourseSelect = (index, course) => {
    setAllSelectedCourses(prev => {
      const newCourses = [...prev[currentYear]];
      const previousCourse = newCourses[index];
      newCourses[index] = course;

      const isDoubleBlocked = course.name.includes("DB");
      const wasDoubleBlocked = previousCourse && previousCourse.name.includes("DB");

      if (isDoubleBlocked && !wasDoubleBlocked) {
        setDoubleBlockedCourses(prev => ({...prev, [currentYear]: prev[currentYear] + 1}));
      } else if (!isDoubleBlocked && wasDoubleBlocked) {
        setDoubleBlockedCourses(prev => ({...prev, [currentYear]: prev[currentYear] - 1}));
      }

      return { ...prev, [currentYear]: newCourses };
    });
    setSearchTerms(prev => { const newTerms = [...prev]; newTerms[index] = ''; return newTerms; });
    setActiveDropdown(null);
  };

  const filteredCourses = useMemo(() => (index, subject) => {
    if (!courses || !courses[subject.toLowerCase()]) return [];
    return courses[subject.toLowerCase()].filter(course => !course.key || course.key.includes(currentYear.toString()));
  }, [courses, currentYear]);

  const toggleDropdown = (index) => {
    setActiveDropdown(activeDropdown === index ? null : index);
    if (activeDropdown !== index) {
      setSearchTerms(prev => { const newTerms = [...prev]; newTerms[index] = ''; return newTerms; });
    }
  };

  const renderDropdown = (index, subject) => (
    <CourseDropdown
      key={index}
      index={index}
      subject={subject}
      courses={filteredCourses(index, subject)}
      searchTerm={searchTerms[index]}
      onSearchChange={handleSearchChange}
      onCourseSelect={handleCourseSelect}
      selectedCourse={allSelectedCourses[currentYear][index]}
      isActive={activeDropdown === index}
      onToggle={() => toggleDropdown(index)}
    />
  );

  if (isReviewing) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen p-4 bg-neutral-900">
        <div className="w-full max-w-4xl bg-neutral-800 rounded-lg shadow-xl p-8">
          <h1 className="gradient-text font-bold text-5xl mb-8 text-center">Review Your Four Year Plan</h1>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {[1, 2, 3, 4].map(year => (
              <div key={year} className="bg-neutral-700 rounded-lg p-6">
                <h2 className="gradient-text font-bold text-3xl mb-4">{getYearName(year)} Year</h2>
                <ul className="list-disc list-inside text-white space-y-2">
                  {allSelectedCourses[year].map((course, index) => (
                    <li key={index} className="text-lg">{course ? course.name : 'Not selected'}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="w-full max-w-3xl mb-8">
        <h1 className="gradient-text font-bold text-5xl mb-6 text-center">Let's build.</h1>
        <p className="text-white text-lg mb-8 text-center">Select your classes for the next four years!</p>
        <div className="mt-8 flex justify-center items-center mb-12">
          <h2 className="gradient-text font-bold text-4xl">{getYearName(currentYear)}</h2>
        </div>
        <h3 className="gradient-text text-xl mb-4 text-center font-semibold">Core Classes</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          {['Math', 'English', 'Social Studies', 'Science'].map((subject, index) => renderDropdown(index, subject))}
        </div>
        <h3 className="gradient-text text-xl mb-4 text-center font-semibold">Electives</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {[4, 5, 6, 7].map(index => doubleBlockedCourses[currentYear] < 4 - index + 4 && renderDropdown(index, 'Elective'))}
        </div>
        <div className="mt-8 flex justify-between items-center">
          {currentYear > 1 && (
            <button onClick={() => handleYearChange('prev')} className="text-white bg-orange-700 px-4 py-2 rounded-lg hover:bg-orange-600 transition-all duration-300">
              BACK
            </button>
          )}
          {currentYear < 4 ? (
            <button onClick={() => handleYearChange('next')} className="text-white bg-orange-700 px-4 py-2 rounded-lg hover:bg-orange-600 transition-all duration-300">
              NEXT
            </button>
          ) : (
            <button onClick={() => setIsReviewing(true)} className="text-white bg-orange-700 px-4 py-2 rounded-lg hover:bg-orange-600 transition-all duration-300">
              REVIEW PLAN
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
