import React, { useState, useEffect } from 'react';
import OpenAI from 'openai';
import courseData from '../assets/data/course-list.json';
import { XMarkIcon } from '@heroicons/react/24/solid';

const Tips = () => {
  const [userInput, setUserInput] = useState('');
  const [chatbotResponse, setChatbotResponse] = useState('');
  const [courses, setCourses] = useState({});
  const [selectedTip, setSelectedTip] = useState(null);

  useEffect(() => {
    setCourses(courseData);
  }, []);

  const openai = new OpenAI({
    apiKey: 'sk-la8GDlMXteiPcwykGJT76wSzhAHmxeMJOg94K4Tgl9T3BlbkFJuzdz0x6rC3BFZlwH8EjnnVYQTdTvgn739E5q0bvrIA',
    dangerouslyAllowBrowser: true
  });

  const handleInputChange = (e) => {
    setUserInput(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await openai.chat.completions.create({
        model: "gpt-3.5-turbo",
        messages: [
          { role: "system", content: "You are a helpful assistant that provides advice on course selection for high school students. Use the provided course data to answer questions accurately." },
          { role: "user", content: "Here's the course data: " + JSON.stringify(courses) },
          { role: "user", content: userInput }
        ],
      });
      setChatbotResponse(response.choices[0].message.content);
    } catch (error) {
      console.error('Error:', error);
      setChatbotResponse('Sorry, there was an error processing your request.');
    }
  };

  const schoolTips = [
    "Consider taking AP classes if you're looking for college credit opportunities.",
    "IB programs offer a more holistic approach to education compared to AP.",
    "Balance your course load between challenging classes and maintaining a good GPA.",
    "Explore electives that align with your interests and potential career paths.",
    "Don't forget to include extracurricular activities in your schedule."
  ];

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="w-full max-w-3xl mb-8">
        <h1 className="gradient-text font-bold text-5xl mb-6 text-center">School Tips</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          {schoolTips.map((tip, index) => (
            <div key={index} onClick={() => setSelectedTip(tip)} className="w-full p-3 bg-neutral-800 text-white rounded-lg cursor-pointer hover:bg-neutral-700 transition-all duration-300">
              {tip.substring(0, 50)}...
            </div>
          ))}
        </div>
        <h2 className="gradient-text font-bold text-4xl mb-6 text-center">Course Selection Assistant</h2>
        <form onSubmit={handleSubmit} className="mb-6">
          <input
            type="text"
            value={userInput}
            onChange={handleInputChange}
            placeholder="Ask about course selection..."
            className="w-full p-3 bg-neutral-800 text-white rounded-lg mb-4 focus:outline-none"
          />
          <button type="submit" className="w-full p-3 bg-orange-700 text-white rounded-lg hover:bg-orange-600 transition-all duration-300">Submit</button>
        </form>
        {chatbotResponse && (
          <div className="bg-neutral-800 p-4 rounded-lg text-white">
            <h3 className="font-bold mb-2">Assistant:</h3>
            <p className="whitespace-pre-wrap">{chatbotResponse}</p>
          </div>
        )}
      </div>
      {selectedTip && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <div className="bg-neutral-800 p-6 rounded-lg max-w-lg w-full relative">
            <button onClick={() => setSelectedTip(null)} className="absolute top-2 right-2 text-white">
              <XMarkIcon className="w-6 h-6" />
            </button>
            <p className="text-white">{selectedTip}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Tips;
