import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import {
  MagnifyingGlassIcon,
  ChevronDownIcon,
  XMarkIcon,
} from "@heroicons/react/24/outline";
import SearchBar from "./SearchBar";

const Courses = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filterOption, setFilterOption] = useState("all");
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [allCourses, setAllCourses] = useState([]);
  const navigate = useNavigate();

  const departmentMap = {
    English: "English Language Arts",
    Mathematics: "Mathematics",
    Science: "Science",
    "Social Studies": "Social Studies",
    LOTE: "Languages Other Than English",
    "Fine Arts": "Fine Arts",
    Dance: "Dance",
    Art: "Art",
    JROTC: "JROTC",
    Animation: "Animation",
    "Business Management & Administration":
      "Business Management & Administration",
    "Computer Science": "Computer Science",
    Engineering: "Engineering",
    Debate: "Debate",
    Robotics: "Robotics",
    "Physical Education": "Physical Education",
    Athletics: "Athletics",
    Offblock: "Offblock",
  };

  const creditMeanings = {
    0.5: "Half Credit Course",
    2: "Two Credit Course",
    3: "Three Credit Course",
    A: "Fall Semester",
    B: "Spring Semester",
    9: "Open to 9th Grade",
    10: "Open to 10th Grade",
    11: "Open to 11th Grade",
    12: "Open to 12th Grade",
    L: "Local Credit",
    "#": "See Note Below",
    "@": "Teacher Approval Required",
    D: "ACC Dual Credit",
    DR: "UT OnRamps",
    AP: "Advanced Placement",
    IB: "International Baccalaureate",
    H: "Honors",
    PreAP: "Pre-AP",
    "1FA + 1PE": "1 Fine Arts and 1 PE Credit",
    "1FA + .5PE": "1 Fine Arts and ½ PE Credit",
  };

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await axios.get(
          `https://sheets.googleapis.com/v4/spreadsheets/19XGBFiAJC3L4CKwxeMaAs3itA7AFeQNoNQbeJAiXZ8U/values/Sheet1!A4:Z500?key=AIzaSyCxATN22mSeipVReXFJDsTleeGGkx8nDTg`
        );

        const htmlResponse = await axios.get(
          "https://docs.google.com/spreadsheets/u/1/d/19XGBFiAJC3L4CKwxeMaAs3itA7AFeQNoNQbeJAiXZ8U/htmlview"
        );
        const parser = new DOMParser();
        const doc = parser.parseFromString(htmlResponse.data, "text/html");
        const courseLinks = {};

        doc.querySelectorAll("a").forEach((link) => {
          if (link.textContent) {
            courseLinks[link.textContent.trim()] = link.href;
          }
        });

        const englishKeywords = ['english', 'elda', 'literature', 'reading', 'writing', 'composition', 'journalism', 'creative writing', 'speech', 'language arts'];
        const socialStudiesKeywords = ['history', 'geography', 'economics', 'government', 'politics', 'social studies', 'psychology', 'sociology', 'world cultures', 'financial literacy', 'ethnic studies'];
        const mathKeywords = ['math', 'algebra', 'geometry', 'calculus', 'statistics', 'precalculus', 'trigonometry', 'quantitative', 'mathematical', 'aqr'];
        const danceKeywords = ['dance', 'ballet', 'choreography', 'modern dance', 'jazz'];
        const artKeywords = ['art', 'drawing', 'painting', 'sculpture', 'ceramics', 'photography', 'digital art', 'design'];
        const jrotcKeywords = ['jrotc', 'military', 'junior reserve', 'cadet', 'aerospace'];
        const csKeywords = ['computer science', 'programming', 'coding', 'java', 'python', 'data structures', 'algorithms'];
        const engineeringKeywords = ['engineering', 'robotics', 'mechanics', 'electronics', 'design and technology', 'cad', 'drafting'];

        const processCourseData = (row, nameIndex, numberIndex, creditsIndex) => {
          if (!row[nameIndex]) return null;

          const courseName = row[nameIndex];
          const courseType = [];
          const lowerName = courseName.toLowerCase();

          ['AP', 'IB', 'Honors', 'Pre-AP', 'Dual', 'Offblock'].forEach(type => {
            if (courseName.includes(type)) courseType.push(type === 'Dual' ? 'Dual Credit' : type);
          });
          if (courseName.includes('Number: ')) {
            courseType.push(courseName.split('Number: ')[1]);
          }

          let department = '';
          if (englishKeywords.some(keyword => lowerName.includes(keyword))) {
            department = 'English Language Arts';
          } else if (socialStudiesKeywords.some(keyword => lowerName.includes(keyword))) {
            department = 'Social Studies';
          } else if (mathKeywords.some(keyword => lowerName.includes(keyword))) {
            department = 'Mathematics';
          } else if (danceKeywords.some(keyword => lowerName.includes(keyword))) {
            department = 'Dance';
          } else if (artKeywords.some(keyword => lowerName.includes(keyword))) {
            department = 'Art';
          } else if (jrotcKeywords.some(keyword => lowerName.includes(keyword))) {
            department = 'JROTC';
          } else if (csKeywords.some(keyword => lowerName.includes(keyword))) {
            department = 'Computer Science';
          } else if (engineeringKeywords.some(keyword => lowerName.includes(keyword))) {
            department = 'Engineering';
          }

          return {
            name: courseName,
            department: department,
            number: row[numberIndex] || "Unknown",
            numberBadge: row[numberIndex] ? `Course #${row[numberIndex]}` : "Unknown Number",
            information: courseLinks[courseName] || "Unknown",
            credits: row[creditsIndex]
              ? row[creditsIndex]
                  .split(",")
                  .map((key) => creditMeanings[key.trim()] || key.trim())
                  .join(", ")
              : "Single Credit Course",
            courseType: courseType,
            link: department,
          };
        };

        const excludeStrings = [
          'course name',
          'course',
          'half credit course',
          'two credit course',
          'three credit course',
          'austin community college dual credit course',
          'university of texas dual credit course'
        ];

        let courses = response.data.values.reduce((acc, row) => {
          if (!row[0] || !row[1]) return acc;

          if (row[1] && !excludeStrings.some(str => row[1].toLowerCase().includes(str))) {
            const course = processCourseData(row, 1, 0, 2);
            if (course) acc.push(course);
          }

          if (row[5]?.trim() && !excludeStrings.some(str => row[5].toLowerCase().includes(str))) {
            const course = processCourseData(row, 5, 4, 6);
            if (course) acc.push(course);
          }

          return acc;
        }, []);

        setAllCourses(courses);
      } catch (error) {
        console.error("Error fetching courses:", error);
      }
    };
    fetchCourses();
  }, []);  useEffect(() => {
    let filtered = allCourses.filter((course) =>
      course.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    if (filterOption !== "all") {
      if (
        ["AP", "IB", "Honors", "Pre-AP", "Dual Credit"].includes(filterOption)
      ) {
        filtered = filtered.filter((course) =>
          course.courseType.includes(filterOption)
        );
      } else {
        filtered = filtered.filter(
          (course) => course.department === filterOption
        );
      }
    }
    setFilteredCourses(filtered);
  }, [searchTerm, filterOption, allCourses]);

  const handleCourseClick = (course) => {
    setSelectedCourse(course);
    toast("Scroll down to see all course information!", {
      position: "top-center",
      autoClose: 3000,
      theme: "dark",
      style: {
        background: "#262626",
        color: "#fff",
        borderRadius: "8px",
        boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
        fontSize: "14px",
      },
      progressStyle: { background: "#FF8C00" },
    });
  };

  const badgeColors = {
    "English Language Arts": "bg-purple-100 text-purple-800 ring-purple-800/20",
    Mathematics: "bg-blue-100 text-blue-800 ring-blue-800/20",
    Science: "bg-green-100 text-green-800 ring-green-700/30",
    "Social Studies": "bg-yellow-100 text-yellow-900 ring-yellow-700/30",
    "Languages Other Than English":
      "bg-pink-100 text-pink-800 ring-pink-800/20",
    "Fine Arts": "bg-indigo-100 text-indigo-800 ring-indigo-800/20",
    "Physical Education": "bg-orange-100 text-orange-800 ring-orange-800/20",
    "Career and Technical Education": "bg-red-100 text-red-800 ring-red-700/20",
    "Other Electives": "bg-gray-100 text-gray-800 ring-gray-800/20",
    AP: "bg-emerald-100 text-emerald-800 ring-emerald-800/20",
    IB: "bg-cyan-100 text-cyan-800 ring-cyan-800/20",
    "Dual Credit": "bg-violet-100 text-violet-800 ring-violet-800/20",
    Offblock: "bg-violet-100 text-violet-800 ring-violet-800/20",
  };

  return (
    <div className="min-h-screen flex flex-col items-center bg-neutral-900 px-4">
      <SearchBar
        onSearch={setSearchTerm}
        onFilter={setFilterOption}
        filterOptions={Object.values(departmentMap).concat([
          "AP",
          "IB",
          "Dual Credit",
        ])}
      />
      <div className="w-full max-w-4xl rounded-lg p-6 mb-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {filteredCourses.map((course, i) => (
            <div
              key={i}
              className="bg-neutral-800 p-4 rounded-lg hover:scale-105 transition-transform duration-200 cursor-pointer"
              onClick={() => handleCourseClick(course)}
            >
              <h3 className="text-lg font-semibold gradient-text">
                {course.name}
              </h3>
              <div className="flex flex-wrap gap-2 mt-2">
                <span
                  className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${
                    badgeColors[course.department] ||
                    "bg-gray-100 text-gray-700 ring-gray-600/20"
                  }`}
                >
                  {course.department}
                </span>
                {course.courseType.map((type, index) => (
                  <span
                    key={index}
                    className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${badgeColors[type]}`}
                  >
                    {type}
                  </span>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
      {selectedCourse && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 overflow-y-auto">
          <div className="bg-neutral-800 p-8 rounded-lg max-w-4xl w-full shadow-2xl transform transition-all duration-300 ease-in-out my-8">
            <h2 className="text-3xl font-bold mb-6 gradient-text">
              {selectedCourse.name}
            </h2>
            <div className="space-y-4 text-neutral-300 max-h-[70vh] overflow-y-auto pr-4">
              <p>
                <span className="font-semibold">Number:</span>{" "}
                {selectedCourse.number || "N/A"}
              </p>
              <p>
                <span className="font-semibold">Key:</span>{" "}
                {selectedCourse.credits || "N/A"}
              </p>
              <p>
                <span className="font-semibold">Department:</span>{" "}
                {selectedCourse.department || "N/A"}
              </p>
              <p>
                <span className="font-semibold">Course Type:</span>{" "}
                {selectedCourse.courseType.join(", ") || "Regular"}
              </p>
              <p>
                <br />
                <span className="font-bold">Information</span> <br />
                <br />
                {selectedCourse.information ? (
                  <iframe
                    src={selectedCourse.information}
                    title="Course Information"
                    className="w-full h-[600px] border-0 rounded"
                  ></iframe>
                ) : (
                  "N/A"
                )}
              </p>
            </div>
            <div className="mt-8 flex justify-end">
              <button
                onClick={() => setSelectedCourse(null)}
                className="text-white bg-orange-700 p-3 rounded-md hover:rotate-[15deg] hover:bg-orange-600 transition-all duration-300 group inline-flex items-center justify-center relative overflow-hidden"
              >
                <span className="relative z-10 transition-all duration-500 transform">
                  <XMarkIcon className="h-6 w-6" />
                </span>
              </button>
            </div>
          </div>
        </div>
      )}{" "}
      <ToastContainer />
    </div>
  );
};

export default Courses;
