import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import courseData from "../assets/data/other-three.json";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { MagnifyingGlassIcon, ChevronDownIcon, XMarkIcon } from '@heroicons/react/24/outline';

const SearchBar = ({ onSearch, onFilter }) => {
const [searchTerm, setSearchTerm] = useState('');
const [filterOption, setFilterOption] = useState('all');
const [isDropdownOpen, setIsDropdownOpen] = useState(false);

const handleSearch = (e) => {
  onSearch(e.target.value);
};

const handleFilter = (option) => {
  setFilterOption(option);
  onFilter(option);
  setIsDropdownOpen(false);
};

const filterOptions = [
  { value: 'all', label: 'All' },
  { value: 'Electives', label: 'Electives' },
  { value: 'Math', label: 'Math' },
  { value: 'English', label: 'English' },
  { value: 'Science', label: 'Science' },
  { value: 'Social Studies', label: 'Social Studies' },
  { value: 'credits-low-high', label: 'Credits (Low to High)' },
  { value: 'credits-high-low', label: 'Credits (High to Low)' },
];

return (
  <div className="w-11/12 sm:w-4/6 mt-10 mb-6 flex items-center">
    <div className="relative flex-grow mr-4">
      <div className="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
        <MagnifyingGlassIcon className="w-5 h-5 text-gray-500 dark:text-gray-400" />
      </div>
      <input
        type="search"
        id="default-search"
        className="block w-full p-4 ps-10 text-sm text-gray-50 shadow-2xl rounded-lg bg-neutral-800"
        placeholder='Search for courses here...'
        value={searchTerm}
        onChange={(e) => {
          setSearchTerm(e.target.value);
          handleSearch(e);
        }}
      />
    </div>
    <div className="relative dropdown-container w-48">
      <div
        onClick={() => setIsDropdownOpen(!isDropdownOpen)}
        className="w-full p-3 bg-neutral-800 text-white rounded-lg cursor-pointer hover:bg-neutral-700 transition-all duration-300 flex justify-between items-center"
      >
        <span className="text-sm">{filterOptions.find(option => option.value === filterOption)?.label || 'Filter'}</span>
        <ChevronDownIcon className={`w-5 h-5 transform transition-transform duration-300 ${isDropdownOpen ? 'rotate-180' : ''}`} />
      </div>
      {isDropdownOpen && (
        <div className="absolute z-50 w-full mt-1 bg-neutral-800 rounded-lg overflow-hidden shadow-2xl">
          <div className="max-h-80 overflow-auto">
            {filterOptions.map((option) => (
              <div
                key={option.value}
                className="p-2 hover:bg-neutral-700 cursor-pointer text-white text-sm"
                onClick={() => handleFilter(option.value)}
              >
                {option.label}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  </div>
);
};

const Courses = () => {
const [searchTerm, setSearchTerm] = useState('');
const [filterOption, setFilterOption] = useState('all');
const [filteredCourses, setFilteredCourses] = useState([]);
const [selectedCourse, setSelectedCourse] = useState(null);
const navigate = useNavigate();

useEffect(() => {
    const allCourses = courseData.departments.flatMap(department => 
        department.courses.map(course => ({...course, department: department.name}))
    );
    let filtered = allCourses.filter(course =>
        course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.number.toLowerCase().includes(searchTerm.toLowerCase())
    );

    if (filterOption !== 'all' && filterOption !== 'credits-low-high' && filterOption !== 'credits-high-low') {
        filtered = filtered.filter(course => course.department === filterOption);
    }

    if (filterOption === 'credits-low-high') {
        filtered.sort((a, b) => a.credits - b.credits);
    } else if (filterOption === 'credits-high-low') {
        filtered.sort((a, b) => b.credits - a.credits);
    }

    setFilteredCourses(filtered);
}, [searchTerm, filterOption]);

const handleSearch = (term) => {
    setSearchTerm(term);
};

const handleFilter = (option) => {
    setFilterOption(option);
};

const handleCourseClick = (course) => {
    setSelectedCourse(course);
    toast("Scroll down to see all course information");
};

const getBadgeColor = (type) => {
    switch(type) {
        case 'department':
            return 'bg-blue-100 text-blue-800 ring-blue-800/20';
        case 'weighted':
            return 'bg-orange-100 text-orange-800 ring-orange-800/20';
        case 'graduationRequirement':
            return 'bg-green-100 text-green-800 ring-green-700/30';
        case 'ncaaApproved':
            return 'bg-purple-100 text-purple-800 ring-purple-800/20';
        case 'dualEnrollment':
            return 'bg-yellow-100 text-yellow-900 ring-yellow-700/30';
        case 'careerCluster':
            return 'bg-pink-100 text-pink-800 ring-pink-800/20';
        case 'careerPathway':
            return 'bg-indigo-100 text-indigo-800 ring-indigo-800/20';
        case 'certifications':
            return 'bg-red-100 text-red-800 ring-red-700/20';
        case 'courseType':
            return 'bg-teal-100 text-teal-800 ring-teal-800/20';
        default:
            return 'bg-gray-100 text-gray-700 ring-gray-600/20';
    }
};

return (
    <div className="min-h-screen flex flex-col items-center bg-neutral-900 px-4">
        <SearchBar onSearch={handleSearch} onFilter={handleFilter} />
        <div className="w-full max-w-4xl rounded-lg p-6 mb-6">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {filteredCourses.map((course, courseIndex) => (
                    <div 
                        key={courseIndex} 
                        className="bg-neutral-800 p-4 rounded-lg hover:scale-105 transition-transform duration-200 cursor-pointer"
                        onClick={() => handleCourseClick(course)}
                    >
                        <h3 className="text-lg font-semibold gradient-text">{course.name} - {course.number}</h3>
                        <div className="flex flex-wrap gap-2 mt-2">
                            <span className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${getBadgeColor('department')}`}>
                                {course.department}
                            </span>
                            {course.weighted && (
                                <span className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${getBadgeColor('weighted')}`}>
                                    Weighted
                                </span>
                            )}
                            {course.graduationRequirement && (
                                <span className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${getBadgeColor('graduationRequirement')}`}>
                                    Graduation Req
                                </span>
                            )}
                            {course.ncaaApproved && (
                                <span className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${getBadgeColor('ncaaApproved')}`}>
                                    NCAA Approved
                                </span>
                            )}
                            {course.dualEnrollment && (
                                <span className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${getBadgeColor('dualEnrollment')}`}>
                                    Dual Enrollment
                                </span>
                            )}
                            {course.careerCluster && (
                                <span className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${getBadgeColor('careerCluster')}`}>
                                    {course.careerCluster}
                                </span>
                            )}
                            {course.careerPathway && (
                                <span className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${getBadgeColor('careerPathway')}`}>
                                    {course.careerPathway}
                                </span>
                            )}
                            {course.certifications && (
                                <span className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${getBadgeColor('certifications')}`}>
                                    Certifications
                                </span>
                            )}
                            {course.courseType && (
                                <span className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${getBadgeColor('courseType')}`}>
                                    {course.courseType}
                                </span>
                            )}
                        </div>
                    </div>
                ))}
            </div>
        </div>
        {selectedCourse && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 overflow-y-auto">
                <div className="bg-neutral-800 p-8 rounded-lg max-w-2xl w-full shadow-2xl transform transition-all duration-300 ease-in-out my-8">
                    <h2 className="text-3xl font-bold mb-6 gradient-text">{selectedCourse.name}</h2>
                    <div className="space-y-4 text-neutral-300 max-h-[60vh] overflow-y-auto pr-4">
                        <p><span className="font-semibold">Number:</span> {selectedCourse.number || 'N/A'}</p>
                        <p><span className="font-semibold">Credits:</span> {selectedCourse.credits || 'N/A'}</p>
                        <p><span className="font-semibold">Department:</span> {selectedCourse.department || 'N/A'}</p>
                        <p><span className="font-semibold">Grade Level:</span> {selectedCourse.gradeLevel || 'N/A'}</p>
                        <p><span className="font-semibold">Prerequisites:</span> {selectedCourse.prerequisites || 'N/A'}</p>
                        <p><span className="font-semibold">Graduation Requirement:</span> {selectedCourse.graduationRequirement ? 'Yes' : 'No'}</p>
                        <p><span className="font-semibold">Weighted:</span> {selectedCourse.weighted ? 'Yes' : 'No'}</p>
                        <p><span className="font-semibold">NCAA Approved:</span> {selectedCourse.ncaaApproved ? 'Yes' : 'No'}</p>
                        <p><span className="font-semibold">Dual Enrollment:</span> {selectedCourse.dualEnrollment ? 'Yes' : 'No'}</p>
                        <p><span className="font-semibold">Career Cluster:</span> {selectedCourse.careerCluster || 'N/A'}</p>
                        <p><span className="font-semibold">Career Pathway:</span> {selectedCourse.careerPathway || 'N/A'}</p>
                        <p><span className="font-semibold">Certifications:</span> {selectedCourse.certifications || 'N/A'}</p>
                        <p><span className="font-semibold">Course Type:</span> {selectedCourse.courseType || 'N/A'}</p>
                        <p className="text-lg leading-relaxed">{selectedCourse.description || 'N/A'}</p>
                    </div>
                    <div className="mt-8 flex justify-end">
                        <button
                          onClick={() => setSelectedCourse(null)}
                          className="text-white bg-orange-700 p-3 rounded-md hover:rotate-[15deg] hover:bg-orange-600 transition-all duration-300 group inline-flex items-center justify-center relative overflow-hidden"
                        >
                          <span className="relative z-10 transition-all duration-500 transform">
                            <XMarkIcon className="h-6 w-6" />
                          </span>
                        </button>
                    </div>
                </div>
            </div>
        )}
        <ToastContainer />
    </div>
)
}

export default Courses;
