import { useState } from "react";
import Courses from '../assets/data/courses.json'

export default function Build() {
  const [selectedInterests, setSelectedInterests] = useState([]);

  const interests = [
    { id: 1, name: 'Arts and Culture' },
    { id: 2, name: 'Science and Technology' },
    { id: 3, name: 'Business and Economics' },
    { id: 4, name: 'Health and Wellness' },
    { id: 5, name: 'Social Sciences' },
    { id: 6, name: 'Sports and Recreation' },
  ];

  const toggleInterest = (id) => {
    setSelectedInterests((prevSelected) => {
      let newSelected;
      if (prevSelected.includes(id)) {
        newSelected = prevSelected.filter((item) => item !== id);
      } else if (prevSelected.length < 3) {
        newSelected = [...prevSelected, id];
      } else {
        newSelected = prevSelected;
      }
      return newSelected;
    });
  };

  const handleSubmit = () => {
    const school = Courses[0]; // Assuming we're working with the first school in the JSON
    const allCourses = school['school-courses'].flatMap(department => department.courses);

    const matchedCourses = allCourses.filter(course => {
      if (selectedInterests.includes(1) && ['Art', 'Theatre Arts', 'Dance', 'Music'].some(dept => course.number.startsWith(dept))) return true;
      if (selectedInterests.includes(2) && ['Science', 'Computer Science', 'Engineering', 'Robotics'].some(dept => course.number.startsWith(dept))) return true;
      if (selectedInterests.includes(3) && ['Finance', 'Marketing', 'Business Management & Administration'].some(dept => course.number.startsWith(dept))) return true;
      if (selectedInterests.includes(4) && ['Health Science', 'Biomedical Science'].some(dept => course.number.startsWith(dept))) return true;
      if (selectedInterests.includes(5) && ['Social Studies', 'Psychology', 'Sociology'].some(dept => course.number.startsWith(dept))) return true;
      if (selectedInterests.includes(6) && ['Sports'].some(dept => course.number.startsWith(dept))) return true;
      return false;
    });

    console.log("Matched courses:", matchedCourses);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="w-full md:w-1/2 mb-8">
        <h1 className="gradient-text font-bold text-4xl mb-4">
          Let's find your interests first.
        </h1>
        <p className="text-white mb-8">
          What fields do you enjoy? (click on up to three interests)
        </p>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {interests.map((interest) => (
            <div
              key={interest.id}
              className={`bg-neutral-800 rounded-lg shadow-lg hover:scale-105 transition-all size-full cursor-pointer ${
                selectedInterests.includes(interest.id) ? 'border-2 border-orange-400' : ''
              }`}
              onClick={() => toggleInterest(interest.id)}
            >
              <div className="p-4">
                <h3 className="gradient-text font-semibold text-lg">{interest.name}</h3>
                <p className="text-white"></p>
              </div>
            </div>
          ))}
        </div>
        <div className="mt-8 text-center">
          <a href="" className="gradient-text">I don't know!</a>
        </div>
        <div className="mt-8 text-center">
          <button
            onClick={handleSubmit}
            className="text-white bg-orange-700 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300"
          >
            SUBMIT
          </button>
        </div>
      </div>
    </div>
  );
}
