import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";

export default function Welcome() {
  const [checkpoint, setCheckpoint] = useState(0);
  const [isSmallScreen, setIsSmallScreen] = useState(false);

  useEffect(() => {
    const checkScreenSize = () => {
      setIsSmallScreen(window.innerWidth < 768); // Adjust this value as needed
    };

    checkScreenSize();
    window.addEventListener('resize', checkScreenSize);

    return () => window.removeEventListener('resize', checkScreenSize);
  }, []);

  const handleNext = () => {
    setCheckpoint((prev) => Math.min(prev + 1, 3));
  };

  const handleBack = () => {
    setCheckpoint((prev) => Math.max(prev - 1, 0));
  };

  const fadeIn = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { duration: 0.5 } },
  };

  const checkpoints = [
    {
      title: "Hey there, RRISD student!",
      content: "Congratulations, you're about to be a high schooler! Before you transition, it is vital to understand what your passion is and how you will move forward with your career. We're going to do something called a <b>four year plan</b>.",
    },
    {
      title: "What is a four year plan?",
      content: "As you progress through high school, you'll need to know which courses seem to be a good fit for you. Additionally, a four year plan can map out what you will do for your freshman, sophomore, junior, and senior year. Take a look at the example on the right!",
    },
    {
      title: "Why is a four year plan important?",
      content: "A four year plan helps you stay organized and focused on your academic and career goals. It ensures you meet graduation requirements and take courses that align with your interests and future aspirations.",
    }
  ];

  const FourYearPlanTable = () => (
    <div className="max-h-[70vh] overflow-y-auto">
      <table className="w-full border-collapse bg-neutral-800 shadow-lg rounded-lg overflow-hidden">
        <thead className="bg-neutral-800 gradient-text text-lg text-white">
          <tr>
            <th className="p-3 text-left">Freshman</th>
            <th className="p-3 text-left">Sophomore</th>
            <th className="p-3 text-left">Junior</th>
            <th className="p-3 text-left">Senior</th>
          </tr>
        </thead>
        <tbody>
          <tr className="bg-neutral-800 text-white">
            <td className="p-3 border-t border-white">TAG Adv English I</td>
            <td className="p-3 border-t border-white">TAG Adv English II</td>
            <td className="p-3 border-t border-white">TAG AP English Literature</td>
            <td className="p-3 border-t border-white">TAG AP English Language</td>
          </tr>
          <tr className="bg-neutral-800 text-white">
            <td className="p-3 border-t border-white">TAG Adv Algebra II</td>
            <td className="p-3 border-t border-white">TAG AP Precalculus</td>
            <td className="p-3 border-t border-white">TAG AP Calculus AB</td>
            <td className="p-3 border-t border-white">TAG AP Calculus BC</td>
          </tr>
          <tr className="bg-neutral-800 text-white">
            <td className="p-3 border-t border-white">TAG Adv Biology</td>
            <td className="p-3 border-t border-white">TAG Adv Chemistry</td>
            <td className="p-3 border-t border-white">TAG AP Physics 1</td>
            <td className="p-3 border-t border-white">TAG AP Physics C</td>
          </tr>
          <tr className="bg-neutral-800 text-white">
            <td className="p-3 border-t border-white">TAG AP Human Geography</td>
            <td className="p-3 border-t border-white">TAG AP World History</td>
            <td className="p-3 border-t border-white">TAG AP U.S. History</td>
            <td className="p-3 border-t border-white">TAG AP Government/Macroeconomics</td>
          </tr>
          <tr className="bg-neutral-800 text-white">
            <td className="p-3 border-t border-white">Debate I</td>
            <td className="p-3 border-t border-white">Debate II</td>
            <td className="p-3 border-t border-white">Debate III</td>
            <td className="p-3 border-t border-white">Independent Study Speech</td>
          </tr>
          <tr className="bg-neutral-800 text-white">
            <td className="p-3 border-t border-white">AP Computer Science Principles</td>
            <td className="p-3 border-t border-white">AP Computer Science A</td>
            <td className="p-3 border-t border-white">Computer Science III</td>
            <td className="p-3 border-t border-white">Mobile App Development</td>
          </tr>
          <tr className="bg-neutral-800 text-white">
            <td className="p-3 border-t border-white">Tennis I</td>
            <td className="p-3 border-t border-white">Tennis II</td>
            <td className="p-3 border-t border-white">Tennis III</td>
            <td className="p-3 border-t border-white">Tennis IV</td>
          </tr>
          <tr className="bg-neutral-800 text-white">
            <td className="p-3 border-t border-white">Principles of Business Marketing and Finance</td>
            <td className="p-3 border-t border-white">Business Information Management</td>
            <td className="p-3 border-t border-white">Business Management and Administration</td>
            <td className="p-3 border-t border-white">Business Law</td>
          </tr>
        </tbody>
      </table>
