  );

  return (
    <div className="flex items-center justify-center min-h-screen">
      <motion.div
        id="welcome"
        className={`mx-10 ${isSmallScreen ? 'flex-col' : 'flex'} justify-between items-center w-full`}
        initial="hidden"
        animate="visible"
        variants={fadeIn}
      >
        <div className={isSmallScreen ? 'w-full' : 'w-1/2'}>
          <AnimatePresence mode="wait">
            <motion.div
              key={checkpoint}
              id={`checkpoint-${checkpoint}`}
              className="transition-opacity duration-500 ease-in-out"
              initial="hidden"
              animate="visible"
              exit="hidden"
              variants={fadeIn}
            >
              <motion.h1
                className="gradient-text font-bold text-4xl mb-4"
                variants={fadeIn}
              >
                {checkpoints[checkpoint].title}
              </motion.h1>
              <motion.p
                className="text-white min-w-60 w-full mb-5"
                variants={fadeIn}
                dangerouslySetInnerHTML={{ __html: checkpoints[checkpoint].content }}
              />
            </motion.div>
          </AnimatePresence>
          <div className="flex space-x-4">
            {checkpoint > 0 && (
              <button
                onClick={handleBack}
                className="text-white bg-emerald-500 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden"
              >
                <span className="relative z-10 transition-all duration-500 group-hover:pl-6">
                  BACK
                </span>
                <span className="absolute left-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:left-4">
                  ←
                </span>
              </button>
            )}
            {checkpoint < checkpoints.length - 1 ? (
              <button
                onClick={handleNext}
                className="text-white bg-emerald-500 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden"
              >
                <span className="relative z-10 transition-all duration-500 group-hover:pr-6">
                  NEXT
                </span>
                <span className="absolute right-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:right-4">
                  →
                </span>
              </button>
            ) : (
              <a href="/build">
                <button
                  onClick={handleNext}
                  className="text-white bg-emerald-500 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden"
                >
                  <span className="relative z-10 transition-all duration-500 group-hover:pr-6">
                    BUILD YOUR FOUR YEAR PLAN 
                  </span>
                  <span className="absolute right-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:right-4">
                    →
                  </span>
                </button>
              </a>
            )}
          </div>
        </div>
        {checkpoint === 1 && (
          <motion.div
            className={`${isSmallScreen ? 'w-full mt-10' : 'w-1/2 pl-10'}`}
            initial="hidden"
            animate="visible"
            variants={fadeIn}
          >
            <FourYearPlanTable />
          </motion.div>
        )}
      </motion.div>
    </div>
  );
}
