import Schools from "../assets/data/schools.json";

export default function Welcome() {
    return (
        <div className="flex items-center min-h-screen">
            <div id="welcome" className="mx-10 flex flex-col md:flex-row justify-between w-full">
                <div className="w-full md:w-1/2" id="intro">
                    <h1 className="gradient-text font-bold text-4xl">Hey there, RRISD student!</h1>
                    <p className="text-white">Congratulations, you're about to be a high schooler! Before you transition, it is vital to understand what your passion is and how you will move forward with our career. We're going to do something called a <b>four year plan</b>.</p>
                </div>
                <div className="w-full md:w-1/2 mt-6 md:mt-0 grid grid-cols-3 gap-4">
                    {Schools.map((school, index) => (
                        <div key={index} className="bg-neutral-800 text-white text-center p-2 rounded-lg hover:scale-105 transition-all duration-500">
                            {school.name}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}
