import React, { useState, useEffect } from "react";
import freshmanData from '../assets/data/freshman.json';

export default function Build() {
  const [currentYear, setCurrentYear] = useState(1);
  const [courses, setCourses] = useState([]);
  const [selectedCourses, setSelectedCourses] = useState(Array(8).fill(null));
  const [searchTerms, setSearchTerms] = useState(Array(8).fill(''));
  const [dropdownOpen, setDropdownOpen] = useState(Array(8).fill(false));

  useEffect(() => {
    setCourses(freshmanData["freshman-year"]);
  }, []);

  const handleYearChange = (direction) => {
    if (direction === 'prev' && currentYear > 1) {
      setCurrentYear(currentYear - 1);
    } else if (direction === 'next' && currentYear < 4) {
      setCurrentYear(currentYear + 1);
    }
  };

  const getYearName = (year) => {
    switch (year) {
      case 1:
        return "Freshman";
      case 2:
        return "Sophomore";
      case 3:
        return "Junior";
      case 4:
        return "Senior";
      default:
        return "";
    }
  };

  const handleSearchChange = (index, value) => {
    const newSearchTerms = [...searchTerms];
    newSearchTerms[index] = value;
    setSearchTerms(newSearchTerms);
  };

  const handleCourseSelect = (index, course) => {
    const newSelectedCourses = [...selectedCourses];
    newSelectedCourses[index] = course;
    setSelectedCourses(newSelectedCourses);

    const newSearchTerms = [...searchTerms];
    newSearchTerms[index] = '';
    setSearchTerms(newSearchTerms);

    const newDropdownOpen = [...dropdownOpen];
    newDropdownOpen[index] = false;
    setDropdownOpen(newDropdownOpen);
  };

  const filteredCourses = (index, isCore) => {
    const coreSubjects = ['Math', 'Science', 'English', 'Social Studies'];
    const availableCourses = courses.flatMap(department => 
      department.courses.filter(course =>
        !selectedCourses.some(selectedCourse => selectedCourse && selectedCourse.name === course.name) &&
        (isCore ? coreSubjects.includes(department.name) : !coreSubjects.includes(department.name))
      )
    );
    return availableCourses.filter(course =>
      course.name.toLowerCase().includes(searchTerms[index].toLowerCase())
    );
  };

  const toggleDropdown = (index) => {
    const newDropdownOpen = [...dropdownOpen];
    newDropdownOpen[index] = !newDropdownOpen[index];
    setDropdownOpen(newDropdownOpen);

    if (newDropdownOpen[index]) {
      const newSearchTerms = [...searchTerms];
      newSearchTerms[index] = '';
      setSearchTerms(newSearchTerms);
    }
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (!event.target.closest('.dropdown-container')) {
        setDropdownOpen(Array(8).fill(false));
      }
    };

    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  const renderDropdowns = (start, end, isCore) => {
    return [...Array(end - start)].map((_, i) => {
      const index = start + i;
      return (
        <div key={index} className="relative dropdown-container">
          <div
            onClick={() => toggleDropdown(index)}
            className="w-full p-3 bg-neutral-800 text-white rounded-lg border border-neutral-700 cursor-pointer hover:bg-neutral-700 transition-all duration-300 flex justify-between items-center"
          >
            <span>{selectedCourses[index] ? selectedCourses[index].name : 'Select a course'}</span>
            <span>↓</span>
          </div>
          {dropdownOpen[index] && (
            <div className="absolute z-10 w-full mt-1 bg-neutral-800 border border-neutral-700 rounded-lg overflow-hidden">
              <input
                type="text"
                value={searchTerms[index]}
                onChange={(e) => handleSearchChange(index, e.target.value)}
                placeholder="Search for a course"
                className="w-full p-3 bg-neutral-700 text-white border-b border-neutral-600 focus:outline-none"
              />
              <div className="max-h-60 overflow-auto">
                {filteredCourses(index, isCore).map((course, courseIndex) => (
                  <div
                    key={courseIndex}
                    className="p-2 hover:bg-neutral-700 cursor-pointer text-white"
                    onClick={() => handleCourseSelect(index, course)}
                  >
                    {course.name}
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      );
    });
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="w-full max-w-3xl mb-8">
        <h1 className="gradient-text font-bold text-5xl mb-6 text-center">
          Let's build.
        </h1>
        <p className="text-white text-lg mb-8 text-center">
          Select courses you'd like to take in each year.
        </p>
        <div className="mt-8 flex justify-between items-center mb-12">
          {currentYear > 1 && (
            <button
              className="text-white bg-orange-800 px-6 py-3 rounded-lg hover:bg-orange-700 transition-all duration-300 group inline-flex items-center relative overflow-hidden text-base font-semibold outline-none"
              onClick={() => handleYearChange('prev')}
            >
              <span className="relative z-10 transition-all duration-500 group-hover:pl-6">
                BACK
              </span>
              <span className="absolute left-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:left-4">
                ←
              </span>
            </button>
          )}
          <h2 className="gradient-text font-bold text-4xl">{getYearName(currentYear)}</h2>
          {currentYear < 4 && (
            <button
              className="text-white bg-orange-800 px-6 py-3 rounded-lg hover:bg-orange-700 transition-all duration-300 group inline-flex items-center relative overflow-hidden text-base font-semibold outline-none"
              onClick={() => handleYearChange('next')}
            >
              <span className="relative z-10 transition-all duration-500 group-hover:pr-6">
                NEXT
              </span>
              <span className="absolute right-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:right-4">
                →
              </span>
            </button>
          )}
        </div>
        <h3 className="gradient-text text-xl mb-4 text-center font-semibold">Core Classes</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          {renderDropdowns(0, 4, true)}
        </div>
        <h3 className="gradient-text text-xl mb-4 text-center">Electives</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {renderDropdowns(4, 8, false)}
        </div>
      </div>
    </div>
  );
}
