import React, { useState, useEffect } from 'react';
import courseData from "../assets/data/other-three.json";
import { useState as useStateSearchBar } from "react";

const SearchBar = () => {
    const [searchTerm, setSearchTerm] = useStateSearchBar('');

    const handleSearch = (e) => {
      e.preventDefault();
      console.log('Searching for:', searchTerm);
    };

    return (
      <form className="w-11/12 sm:w-3/6 mt-10" onSubmit={handleSearch}>
        <label
          htmlFor="default-search"
          className="mb-2 text-sm font-medium text-gray-900 sr-only"
        >
          Search
        </label>
        <div className="relative">
          <div className="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
            <svg
              className="w-4 h-4 text-gray-500 dark:text-gray-400"
              aria-hidden="true"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 20 20"
            >
              <path
                stroke="currentColor"
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="m19 19-4-4m0-7A7 7 0 1 1 1 8a7 7 0 0 1 14 0Z"
              />
            </svg>
          </div>
          <input
            type="search"
            id="default-search"
            className="block w-full p-4 ps-10 text-sm text-gray-50 shadow-2xl rounded-lg bg-neutral-700"
            placeholder='Search for courses here...'
            required
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button
            type="submit"
            className="text-white absolute end-2.5 bottom-2 bg-orange-700 hover:bg-orange-700 font-medium rounded-lg text-sm px-4 py-2"
          >
            Search
          </button>
        </div>
      </form>
    );
};

const Courses = () => {
      const [searchTerm, setSearchTerm] = useState('');
      const [filteredCourses, setFilteredCourses] = useState([]);

      useEffect(() => {
          const filtered = courseData["sophomore-year"].map(department => ({
              ...department,
              courses: department.courses.filter(course =>
                  course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                  course.number.toLowerCase().includes(searchTerm.toLowerCase())
              )
          })).filter(department => department.courses.length > 0);

          setFilteredCourses(filtered);
      }, [searchTerm]);

      const handleSearch = (term) => {
          setSearchTerm(term);
      };

      return (
          <div className="min-h-screen flex flex-col items-center bg-neutral-900">
              <SearchBar onSearch={handleSearch} />
              {filteredCourses.map((department, index) => (
                  <div key={index} className="w-full max-w-4xl bg-neutral-800 rounded-lg shadow-md p-6 mb-6">
                      <h2 className="text-2xl font-bold text-white mb-4">{department.department}</h2>
                      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                          {department.courses.map((course, courseIndex) => (
                              <div key={courseIndex} className="bg-neutral-700 p-4 rounded-lg">
                                  <h3 className="text-lg font-semibold text-white">{course.name}</h3>
                                  <p className="text-neutral-300">Number: {course.number}</p>
                                  <p className="text-neutral-300">Credits: {course.credits}</p>
                              </div>
                          ))}
                      </div>
                  </div>
              ))}
          </div>
      )
}

export default Courses;
