import React, { useState, useEffect, useCallback, useMemo } from "react";
import PropTypes from 'prop-types';
import otherData from "../assets/data/other-three.json";
import freshmanData from "../assets/data/freshman.json";

const CourseDropdown = ({ index, subject, courses, searchTerm, onSearchChange, onCourseSelect, selectedCourse, isActive, onToggle }) => {
  const filteredCourses = useMemo(() => courses.filter(course => course.name.toLowerCase().includes(searchTerm.toLowerCase())), [courses, searchTerm]);

  return (
    <div className="relative dropdown-container">
      <div onClick={onToggle} className="w-full p-3 bg-neutral-800 text-white rounded-lg cursor-pointer hover:bg-neutral-700 transition-all duration-300 flex justify-between items-center">
        <span>{selectedCourse ? selectedCourse.name : `${subject}`}</span>
        <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={`transform transition-transform duration-300 ${isActive ? 'rotate-180' : ''}`}>
          <polyline points="6 9 12 15 18 9"></polyline>
        </svg>
      </div>
      {isActive && (
        <div className="absolute z-50 w-full mt-1 bg-neutral-800 rounded-lg overflow-hidden shadow-2xl">
          <input type="text" value={searchTerm} onChange={(e) => onSearchChange(index, e.target.value)} placeholder={`Search for a ${subject}`} className="w-full p-3 bg-neutral-700 text-white focus:outline-none shadow-2xl" />
          <div className="max-h-60 overflow-auto">
            {filteredCourses.map((course, courseIndex) => (
              <div key={courseIndex} className="p-2 hover:bg-neutral-700 cursor-pointer text-white" onClick={() => onCourseSelect(index, course)}>{course.name}</div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

CourseDropdown.propTypes = {
  index: PropTypes.number.isRequired,
  subject: PropTypes.string.isRequired,
  courses: PropTypes.array.isRequired,
  searchTerm: PropTypes.string.isRequired,
  onSearchChange: PropTypes.func.isRequired,
  onCourseSelect: PropTypes.func.isRequired,
  selectedCourse: PropTypes.object,
  isActive: PropTypes.bool.isRequired,
  onToggle: PropTypes.func.isRequired,
};

export default function Build({ courses: initialCourses }) {
  const [currentYear, setCurrentYear] = useState(1);
  const [courses, setCourses] = useState(initialCourses);
  const [allSelectedCourses, setAllSelectedCourses] = useState({ 1: Array(8).fill(null), 2: Array(8).fill(null), 3: Array(8).fill(null), 4: Array(8).fill(null) });
  const [searchTerms, setSearchTerms] = useState(Array(8).fill(''));
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [doubleBlockedCourses, setDoubleBlockedCourses] = useState({ 1: 0, 2: 0, 3: 0, 4: 0 });

  useEffect(() => {
    const yearData = { 1: freshmanData["freshman-year"], 2: otherData.departments, 3: otherData.departments, 4: otherData.departments };
    setCourses(yearData[currentYear]);
  }, [currentYear]);

  const handleYearChange = (direction) => {
    if (direction === 'prev' && currentYear > 1) setCurrentYear(currentYear - 1);
    else if (direction === 'next' && currentYear < 4) {
      if (currentYear === 1) console.log("Freshman Year Courses:", allSelectedCourses[1]);
      setCurrentYear(currentYear + 1);
    }
    setSearchTerms(Array(8).fill(''));
    setActiveDropdown(null);
  };

  const getYearName = (year) => ["", "Freshman", "Sophomore", "Junior", "Senior"][year];

  const handleSearchChange = useCallback((index, value) => {
    setSearchTerms(prev => { const newSearchTerms = [...prev]; newSearchTerms[index] = value; return newSearchTerms; });
  }, []);

  const handleCourseSelect = (index, course) => {
    setAllSelectedCourses(prev => {
      const newSelectedCourses = [...prev[currentYear]];
      const previousCourse = newSelectedCourses[index];
      newSelectedCourses[index] = course;

      const isDoubleBlocked = course.name.includes("DB");
      const wasDoubleBlocked = previousCourse && previousCourse.name.includes("DB");

      if (isDoubleBlocked && !wasDoubleBlocked) {
        setDoubleBlockedCourses(prev => ({...prev, [currentYear]: prev[currentYear] + 1}));
        for (let i = 7; i >= 4; i--) {
          if (newSelectedCourses[i] && !newSelectedCourses[i].name.includes("DB")) {
            newSelectedCourses[i] = null;
            break;
          }
        }
      } else if (!isDoubleBlocked && wasDoubleBlocked) {
        setDoubleBlockedCourses(prev => ({...prev, [currentYear]: prev[currentYear] - 1}));
        for (let i = 4; i <= 7; i++) {
          if (!newSelectedCourses[i]) {
            newSelectedCourses[i] = { name: 'Elective' };
            break;
          }
        }
      }

      return { ...prev, [currentYear]: newSelectedCourses };
    });

    setSearchTerms(prev => { const newSearchTerms = [...prev]; newSearchTerms[index] = ''; return newSearchTerms; });
    setActiveDropdown(null);
  };

  const filteredCourses = useMemo(() => (index, subject) => {
    if (!courses || !Array.isArray(courses)) return [];

    const selectedCourses = allSelectedCourses[currentYear].filter(Boolean);
    const selectedCourseNames = selectedCourses.map(course => course.name.split(' ')[0]);
    const previousYearCourses = currentYear > 1 ? allSelectedCourses[currentYear - 1].filter(Boolean) : [];
    const previousYearCourseNames = previousYearCourses.map(course => course.name.split(' ')[0]);

    return courses.flatMap(department => {
      if (department && Array.isArray(department.courses)) {
        return department.courses.filter(course => {
          const courseName = course.name.split(' ')[0];
          const isAlreadySelected = allSelectedCourses[currentYear].some(selectedCourse => selectedCourse && selectedCourse.name === course.name);
          const isRelatedCourse = selectedCourseNames.includes(courseName) && subject === 'Elective';
          const isPreviousYearCourse = previousYearCourseNames.includes(courseName);

          return !isAlreadySelected && !isRelatedCourse && !isPreviousYearCourse &&
            (subject === 'Elective' ? !['Math', 'English', 'Social Studies', 'Science'].includes(department.department) : department.department === subject);
        });
      }
      return [];
    });
  }, [courses, currentYear, allSelectedCourses]);

  const toggleDropdown = (index) => {
    setActiveDropdown(activeDropdown === index ? null : index);
    if (activeDropdown !== index) {
      setSearchTerms(prev => { const newSearchTerms = [...prev]; newSearchTerms[index] = ''; return newSearchTerms; });
    }
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (!event.target.closest('.dropdown-container')) setActiveDropdown(null);
    };
    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, []);

  const renderDropdown = (index, subject) => (
    <CourseDropdown
      key={index}
      index={index}
      subject={subject}
      courses={filteredCourses(index, subject)}
      searchTerm={searchTerms[index]}
      onSearchChange={handleSearchChange}
      onCourseSelect={handleCourseSelect}
      selectedCourse={allSelectedCourses[currentYear][index]}
      isActive={activeDropdown === index}
      onToggle={() => toggleDropdown(index)}
    />
  );

  const handleSubmit = () => console.log("All selected courses:", allSelectedCourses);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="w-full max-w-3xl mb-8">
        <h1 className="gradient-text font-bold text-5xl mb-6 text-center">Let's build.</h1>
        <p className="text-white text-lg mb-8 text-center">Select your classes for the next four years!</p>
        <div className="mt-8 flex justify-center items-center mb-12">
          <h2 className="gradient-text font-bold text-4xl">{getYearName(currentYear)}</h2>
        </div>
        <h3 className="gradient-text text-xl mb-4 text-center font-semibold">Core Classes</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          {renderDropdown(0, 'Math')}
          {renderDropdown(1, 'English')}
          {renderDropdown(2, 'Social Studies')}
          {renderDropdown(3, 'Science')}
        </div>
        <h3 className="gradient-text text-xl mb-4 text-center font-semibold">Electives</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {doubleBlockedCourses[currentYear] < 7 && renderDropdown(4, 'Elective')}
          {doubleBlockedCourses[currentYear] < 5 && renderDropdown(5, 'Elective')}
          {doubleBlockedCourses[currentYear] < 3 && renderDropdown(6, 'Elective')}
          {doubleBlockedCourses[currentYear] < 1 && renderDropdown(7, 'Elective')}
        </div>
        <div className="mt-8 flex justify-between items-center">
          {currentYear > 1 && (
            <button onClick={() => handleYearChange('prev')} className="text-white bg-orange-700 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden">
              <span className="relative z-10 transition-all duration-500 group-hover:pl-6">BACK</span>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="absolute left-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:left-2">
                <polyline points="15 18 9 12 15 6"></polyline>
              </svg>
            </button>
          )}
          {currentYear < 4 ? (
            <button onClick={() => handleYearChange('next')} className="text-white bg-orange-700 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden">
              <span className="relative z-10 transition-all duration-500 group-hover:pr-6">NEXT</span>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="absolute right-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:right-2">
                <polyline points="9 18 15 12 9 6"></polyline>
              </svg>
            </button>
          ) : (
            <button onClick={() => handleSubmit()} className="text-white bg-orange-700 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden">
              <span className="relative z-10 transition-all duration-500 group-hover:pr-6">REVIEW</span>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="absolute right-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:right-2">
                <polyline points="9 18 15 12 9 6"></polyline>
              </svg>
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

Build.propTypes = {
  courses: PropTypes.array.isRequired,
};
