import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { MagnifyingGlassIcon, ChevronDownIcon, XMarkIcon } from '@heroicons/react/24/outline';

const SearchBar = ({ onSearch, onFilter }) => {
  // ... (SearchBar component remains unchanged)
};

const Courses = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [filterOption, setFilterOption] = useState('all');
    const [filteredCourses, setFilteredCourses] = useState([]);
    const [selectedCourse, setSelectedCourse] = useState(null);
    const [allCourses, setAllCourses] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
      const fetchCourses = async () => {
        try {
          const response = await axios.get('/src/assets/data/data.txt');
          const lines = response.data.split('\n');
          const courses = [];
          let currentDepartment = '';

          for (let i = 0; i < lines.length; i++) {
            const line = lines[i].trim();
            if (line.endsWith(':')) {
              currentDepartment = line.slice(0, -1);
            } else if (line && !line.startsWith('#') && !line.startsWith('Num')) {
              const [number, name, key] = line.split('\t').map(item => item.trim());
              if (number && name) {
                courses.push({
                  number,
                  name,
                  key: key || '',
                  department: currentDepartment,
                });
              }
            }
          }

          setAllCourses(courses);
        } catch (error) {
          console.error('Error fetching courses:', error);
        }
      };

      fetchCourses();
    }, []);

    useEffect(() => {
      let filtered = allCourses.filter(course =>
        course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.number.toLowerCase().includes(searchTerm.toLowerCase())
      );

      if (filterOption !== 'all') {
        filtered = filtered.filter(course => course.department === filterOption);
      }

      setFilteredCourses(filtered);
    }, [searchTerm, filterOption, allCourses]);

    const handleSearch = (term) => {
      setSearchTerm(term);
    };

    const handleFilter = (option) => {
      setFilterOption(option);
    };

    const handleCourseClick = (course) => {
      setSelectedCourse(course);
      toast("Scroll down to see all course information!", {
        position: "top-center",
        autoClose: 3000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
        style: {
          background: '#262626',
          color: '#fff',
          borderRadius: '8px',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
          fontSize: '14px',
        },
        progressStyle: {
          background: '#FF8C00',
        },
      });
    };

    const getBadgeColor = (department) => {
      switch(department) {
        case 'Math': return 'bg-blue-100 text-blue-800 ring-blue-800/20';
        case 'Science': return 'bg-green-100 text-green-800 ring-green-700/30';
        case 'Social Studies': return 'bg-yellow-100 text-yellow-900 ring-yellow-700/30';
        case 'English/Language Arts': return 'bg-purple-100 text-purple-800 ring-purple-800/20';
        case 'Electives': return 'bg-red-100 text-red-800 ring-red-700/20';
        default: return 'bg-gray-100 text-gray-700 ring-gray-600/20';
      }
    };

    return (
      <div className="min-h-screen flex flex-col items-center bg-neutral-900 px-4">
        <SearchBar onSearch={handleSearch} onFilter={handleFilter} />
        <div className="w-full max-w-4xl rounded-lg p-6 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {filteredCourses.map((course, courseIndex) => (
              <div 
                key={courseIndex} 
                className="bg-neutral-800 p-4 rounded-lg hover:scale-105 transition-transform duration-200 cursor-pointer"
                onClick={() => handleCourseClick(course)}
              >
                <h3 className="text-lg font-semibold gradient-text">{course.name}</h3>
                <div className="flex flex-wrap gap-2 mt-2">
                  <span className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${getBadgeColor(course.department)}`}>
                    {course.department}
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>
        {selectedCourse && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 overflow-y-auto">
            <div className="bg-neutral-800 p-8 rounded-lg max-w-2xl w-full shadow-2xl transform transition-all duration-300 ease-in-out my-8">
              <h2 className="text-3xl font-bold mb-6 gradient-text">{selectedCourse.name}</h2>
              <div className="space-y-4 text-neutral-300 max-h-[60vh] overflow-y-auto pr-4">
                <p><span className="font-semibold">Number:</span> {selectedCourse.number || 'N/A'}</p>
                <p><span className="font-semibold">Key:</span> {selectedCourse.key || 'N/A'}</p>
                <p><span className="font-semibold">Department:</span> {selectedCourse.department || 'N/A'}</p>
              </div>
              <div className="mt-8 flex justify-end">
                <button
                  onClick={() => setSelectedCourse(null)}
                  className="text-white bg-orange-700 p-3 rounded-md hover:rotate-[15deg] hover:bg-orange-600 transition-all duration-300 group inline-flex items-center justify-center relative overflow-hidden"
                >
                  <span className="relative z-10 transition-all duration-500 transform">
                    <XMarkIcon className="h-6 w-6" />
                  </span>
                </button>
              </div>
            </div>
          </div>
        )}
        <ToastContainer />
      </div>
    )
}

export default Courses;
