import React, { useState } from 'react';
import SearchBar from "./SearchBar";
import courseData from "../assets/data/other-three.json";

const Courses = () => {
    const [searchTerm, setSearchTerm] = useState('');

    const filteredCourses = courseData["sophomore-year"].map(department => ({
        ...department,
        courses: department.courses.filter(course =>
            course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            course.number.toLowerCase().includes(searchTerm.toLowerCase())
        )
    })).filter(department => department.courses.length > 0);

    const handleSearch = (term) => {
        setSearchTerm(term);
    };

    return (
        <div className="min-h-screen flex flex-col items-center bg-neutral-900">
            <SearchBar onSearch={handleSearch} />
            {filteredCourses.map((department, index) => (
                <div key={index} className="w-full max-w-4xl bg-neutral-800 rounded-lg shadow-md p-6 mb-6">
                    <h2 className="text-2xl font-bold text-white mb-4">{department.department}</h2>
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                        {department.courses.map((course, courseIndex) => (
                            <div key={courseIndex} className="bg-neutral-700 p-4 rounded-lg">
                                <h3 className="text-lg font-semibold text-white">{course.name}</h3>
                                <p className="text-neutral-300">Number: {course.number}</p>
                                <p className="text-neutral-300">Credits: {course.credits}</p>
                            </div>
                        ))}
                    </div>
                </div>
            ))}
        </div>
    )
}

export default Courses;
