import React, { useState, useEffect } from 'react';
import OpenAI from 'openai';
import courseData from '../assets/data/course-list.json';
import { XMarkIcon } from '@heroicons/react/24/outline';

const Tips = () => {
  const [userInput, setUserInput] = useState('');
  const [chatbotResponse, setChatbotResponse] = useState('');
  const [courses, setCourses] = useState({});
  const [selectedTip, setSelectedTip] = useState(null);

  useEffect(() => {
    setCourses(courseData);
  }, []);

  const openai = new OpenAI({
    apiKey: 'sk-la8GDlMXteiPcwykGJT76wSzhAHmxeMJOg94K4Tgl9T3BlbkFJuzdz0x6rC3BFZlwH8EjnnVYQTdTvgn739E5q0bvrIA',
    dangerouslyAllowBrowser: true
  });

  const handleInputChange = (e) => {
    setUserInput(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await openai.chat.completions.create({
        model: "gpt-3.5-turbo",
        messages: [
          { role: "system", content: "You are a helpful assistant that provides advice on course selection for high school students. Use the provided course data to answer questions accurately." },
          { role: "user", content: "Here's the course data: " + JSON.stringify(courses) },
          { role: "user", content: userInput }
        ],
      });
      setChatbotResponse(response.choices[0].message.content);
    } catch (error) {
      console.error('Error:', error);
      setChatbotResponse('Sorry, there was an error processing your request.');
    }
  };

  const schoolTips = [
    "Consider taking AP classes if you're looking for college credit opportunities.",
    "IB programs offer a more holistic approach to education compared to AP.",
    "Balance your course load between challenging classes and maintaining a good GPA.",
    "Explore electives that align with your interests and potential career paths.",
    "Don't forget to include extracurricular activities in your schedule."
  ];

  return (
    <div className="min-h-screen flex flex-col items-center bg-neutral-900 px-4">
      <div className="w-full max-w-4xl mt-10 mb-6">
        <h1 className="text-5xl font-bold text-center mb-12 gradient-text">School Tips & Course Selection</h1>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          <div>
            <h2 className="text-3xl font-semibold mb-6 gradient-text">Helpful Tips</h2>
            <div className="grid gap-4">
              {schoolTips.map((tip, index) => (
                <div 
                  key={index} 
                  onClick={() => setSelectedTip(tip)} 
                  className="p-4 bg-neutral-800 rounded-lg cursor-pointer hover:bg-neutral-700 transition-all duration-300 text-white"
                >
                  {tip.substring(0, 50)}...
                </div>
              ))}
            </div>
          </div>

          <div>
            <h2 className="text-3xl font-semibold mb-6 gradient-text">Course Selection Assistant</h2>
            <form onSubmit={handleSubmit} className="mb-6">
              <input
                type="text"
                value={userInput}
                onChange={handleInputChange}
                placeholder="Ask about course selection..."
                className="w-full p-4 ps-10 text-sm text-gray-50 shadow-2xl rounded-lg bg-neutral-800 mb-4"
              />
              <button type="submit" className="w-full p-3 bg-orange-700 rounded-lg hover:bg-orange-600 transition-all duration-300">
                Get Advice
              </button>
            </form>
            {chatbotResponse && (
              <div className="bg-neutral-800 p-6 rounded-lg">
                <h3 className="font-bold mb-2 gradient-text">Assistant:</h3>
                <p className="whitespace-pre-wrap text-neutral-300">{chatbotResponse}</p>
              </div>
            )}
          </div>
        </div>
      </div>

      {selectedTip && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 overflow-y-auto">
          <div className="bg-neutral-800 p-8 rounded-lg max-w-2xl w-full shadow-2xl transform transition-all duration-300 ease-in-out my-8 relative">
            <h3 className="text-3xl font-bold mb-6 gradient-text">Tip Details</h3>
            <p className="text-neutral-300 text-lg leading-relaxed">{selectedTip}</p>
            <div className="mt-8 flex justify-end">
              <button
                onClick={() => setSelectedTip(null)}
                className="text-white bg-orange-700 p-3 rounded-md hover:rotate-[15deg] hover:bg-orange-600 transition-all duration-300 group inline-flex items-center justify-center relative overflow-hidden"
              >
                <span className="relative z-10 transition-all duration-500 transform">
                  <XMarkIcon className="h-6 w-6" />
                </span>
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Tips;
