import { useState, useEffect } from "react";

export default function Build() {
  const [currentYear, setCurrentYear] = useState(9);
  const [yearPlan, setYearPlan] = useState({
    9: [],
    10: [],
    11: [],
    12: []
  });
  const [courses, setCourses] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCourses, setSelectedCourses] = useState([]);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const yearFiles = {
          9: 'freshman',
          10: 'sophomore',
          11: 'junior',
          12: 'senior'
        };
        const response = await fetch(`/src/assets/data/${yearFiles[currentYear]}`);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response;
        setCourses(data);
      } catch (error) {
        console.error('Error fetching course data:', error);
        setCourses([]); // Set courses to an empty array in case of error
      }
    };

    fetchCourses();
  }, [currentYear]);

  const handleCourseSelection = (course) => {
    setSelectedCourses(prevSelected => {
      if (prevSelected.includes(course)) {
        return prevSelected.filter(c => c !== course);
      } else {
        return [...prevSelected, course];
      }
    });
  };

  const handleNext = () => {
    setYearPlan(prevPlan => ({
      ...prevPlan,
      [currentYear]: selectedCourses
    }));
    if (currentYear < 12) {
      setCurrentYear(prevYear => prevYear + 1);
      setSelectedCourses([]);
    } else {
      console.log("Four Year Plan:", yearPlan);
    }
  };

  const handleBack = () => {
    if (currentYear > 9) {
      setCurrentYear(prevYear => prevYear - 1);
      setSelectedCourses(yearPlan[currentYear - 1] || []);
    }
  };

  const yearNames = {
    9: "Freshman",
    10: "Sophomore",
    11: "Junior",
    12: "Senior"
  };

  const filteredCourses = courses.filter(course => 
    course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    course.department.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="w-full md:w-1/2 mb-8">
        <h1 className="gradient-text font-bold text-4xl mb-4">
          Plan your {yearNames[currentYear]} year
        </h1>
        <p className="text-white mb-8">
          Select courses you'd like to take in your {yearNames[currentYear]} year:
        </p>
        <input
          type="text"
          placeholder="Search courses..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full p-2 mb-4 bg-neutral-700 text-white rounded-lg"
        />
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {filteredCourses.map((course) => (
            <div
              key={course.id}
              className={`bg-neutral-800 rounded-lg shadow-lg hover:scale-105 transition-all cursor-pointer ${
                selectedCourses.includes(course) ? "border-2 border-orange-400" : ""
              }`}
              onClick={() => handleCourseSelection(course)}
            >
              <div className="p-4">
                <h3 className="gradient-text font-semibold text-lg">
                  {course.name}
                </h3>
                <p className="text-white">{course.department}</p>
              </div>
            </div>
          ))}
        </div>
        <div className="mt-8 flex justify-between">
          <button
            onClick={handleBack}
            className="text-white bg-orange-800 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden text-sm sm:text-base"
            disabled={currentYear === 9}
          >
            <span className="relative z-10 transition-all duration-500 group-hover:pl-6">
              BACK
            </span>
            <span className="absolute left-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:left-4">
              ←
            </span>
          </button>
          <button
            onClick={handleNext}
            className="text-white bg-orange-800 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden text-sm sm:text-base"
          >
            <span className="relative z-10 transition-all duration-500 group-hover:pr-6">
              {currentYear === 12 ? "FINISH" : "NEXT"}
            </span>
            <span className="absolute right-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:right-4">
              →
            </span>
          </button>
        </div>
        {currentYear === 12 && (
          <div className="mt-8">
            <h2 className="gradient-text font-bold text-3xl mb-4">Your Four Year Plan</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {Object.entries(yearPlan).map(([year, courses]) => (
                <div key={year} className="bg-neutral-800 rounded-lg p-4">
                  <h3 className="gradient-text font-semibold text-xl mb-2">{yearNames[year]}</h3>
                  <ul className="text-white">
                    {courses.map((course, index) => (
                      <li key={index}>{course.name}</li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
