import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import courseData from "../assets/data/other-three.json";

const SearchBar = ({ onSearch, onFilter }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [filterOption, setFilterOption] = useState('all');
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);

    const handleSearch = (e) => {
      onSearch(e.target.value);
    };

    const handleFilter = (option) => {
      setFilterOption(option);
      onFilter(option);
      setIsDropdownOpen(false);
    };

    const filterOptions = [
      { value: 'all', label: 'All' },
      { value: 'Elective', label: 'Electives' },
      { value: 'Math', label: 'Math' },
      { value: 'English', label: 'English' },
      { value: 'Science', label: 'Science' },
      { value: 'Social Studies', label: 'Social Studies' },
      { value: 'credits-low-high', label: 'Credits (Low to High)' },
      { value: 'credits-high-low', label: 'Credits (High to Low)' },
    ];

    return (
      <div className="w-full max-w-4xl mt-10 mb-6 flex flex-col sm:flex-row items-center gap-4">
        <div className="relative flex-grow w-full">
          <div className="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
            <svg
              className="w-5 h-5 text-orange-500"
              aria-hidden="true"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 20 20"
            >
              <path
                stroke="currentColor"
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="m19 19-4-4m0-7A7 7 0 1 1 1 8a7 7 0 0 1 14 0Z"
              />
            </svg>
          </div>
          <input
            type="search"
            id="default-search"
            className="block w-full p-4 ps-10 text-sm text-orange-500 shadow-lg rounded-full bg-neutral-800 border border-orange-500 focus:ring-2 focus:ring-orange-500 focus:outline-none transition-all duration-300"
            placeholder='Search for courses here...'
            value={searchTerm}
            onChange={(e) => {
              setSearchTerm(e.target.value);
              handleSearch(e);
            }}
          />
        </div>
        <div className="relative w-full sm:w-auto">
          <button
            className="w-full sm:w-auto p-4 text-sm text-orange-500 shadow-lg rounded-full bg-neutral-800 border border-orange-500 flex items-center justify-center hover:bg-neutral-700 transition-all duration-300"
            onClick={() => setIsDropdownOpen(!isDropdownOpen)}
          >
            {filterOptions.find(option => option.value === filterOption)?.label || 'Filter'}
            <svg className={`w-4 h-4 ml-2 transition-transform duration-300 ${isDropdownOpen ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
            </svg>
          </button>
          {isDropdownOpen && (
            <div className="absolute right-0 mt-2 w-48 rounded-xl shadow-lg bg-neutral-800 ring-1 ring-black ring-opacity-5 z-10">
              <div className="py-1" role="menu" aria-orientation="vertical" aria-labelledby="options-menu">
                {filterOptions.map((option) => (
                  <button
                    key={option.value}
                    className="block w-full text-left px-4 py-2 text-sm text-orange-500 hover:bg-neutral-700 transition-all duration-200"
                    onClick={() => handleFilter(option.value)}
                  >
                    {option.label}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
    );
};

const Courses = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [filterOption, setFilterOption] = useState('all');
    const [filteredCourses, setFilteredCourses] = useState([]);
    const [selectedCourse, setSelectedCourse] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
      const allCourses = courseData["sophomore-year"].flatMap(department => 
        department.courses.map(course => ({...course, department: department.department}))
      );
      let filtered = allCourses.filter(course =>
        course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.number.toLowerCase().includes(searchTerm.toLowerCase())
      );

      if (filterOption !== 'all' && filterOption !== 'credits-low-high' && filterOption !== 'credits-high-low') {
        filtered = filtered.filter(course => course.department === filterOption);
      }

      if (filterOption === 'credits-low-high') {
        filtered.sort((a, b) => a.credits - b.credits);
      } else if (filterOption === 'credits-high-low') {
        filtered.sort((a, b) => b.credits - a.credits);
      }

      setFilteredCourses(filtered);
    }, [searchTerm, filterOption]);

    const handleSearch = (term) => {
      setSearchTerm(term);
    };

    const handleFilter = (option) => {
      setFilterOption(option);
    };

    const handleCourseClick = (course) => {
      setSelectedCourse(course);
    };

    const getBadgeColor = (department) => {
      switch(department) {
        case 'Math': return 'bg-blue-900 text-blue-300 ring-blue-500';
        case 'Science': return 'bg-green-900 text-green-300 ring-green-500';
        case 'Social Studies': return 'bg-yellow-900 text-yellow-300 ring-yellow-500';
        case 'English': return 'bg-purple-900 text-purple-300 ring-purple-500';
        case 'Elective': return 'bg-red-900 text-red-300 ring-red-500';
        default: return 'bg-gray-800 text-gray-300 ring-gray-500';
      }
    };

    return (
      <div className="min-h-screen flex flex-col items-center bg-neutral-900 px-4 py-8">
        <SearchBar onSearch={handleSearch} onFilter={handleFilter} />
        <div className="w-full max-w-6xl rounded-xl p-6 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredCourses.map((course, courseIndex) => (
              <div 
                key={courseIndex} 
                className="bg-neutral-800 p-6 rounded-xl shadow-lg hover:shadow-xl hover:scale-105 transition-all duration-300 cursor-pointer"
                onClick={() => handleCourseClick(course)}
              >
                <h3 className="text-xl font-bold mb-3 text-orange-500">{course.name}</h3>
                <p className="text-gray-400 mb-3">{course.number}</p>
                <div className="flex flex-wrap gap-2 mt-2">
                  <span className={`inline-flex items-center rounded-full px-3 py-1 text-xs font-medium ring-1 ring-inset ${getBadgeColor(course.department)}`}>
                    {course.department}
                  </span>
                  {course.department !== 'Elective' && (
                    <span className="inline-flex items-center rounded-full px-3 py-1 text-xs font-medium bg-orange-900 text-orange-300 ring-1 ring-inset ring-orange-500">
                      Weighted
                    </span>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
        {selectedCourse && (
          <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center p-4 z-50">
            <div className="bg-neutral-800 p-8 rounded-xl max-w-2xl w-full shadow-2xl">
              <h2 className="text-3xl font-bold mb-4 text-orange-500">{selectedCourse.name}</h2>
              <p className="text-gray-300 mb-2 text-lg">Number: {selectedCourse.number}</p>
              <p className="text-gray-300 mb-2 text-lg">Credits: {selectedCourse.credits}</p>
              <p className="text-gray-300 mb-2 text-lg">Department: {selectedCourse.department}</p>
              <p className="text-gray-300 mb-6 text-lg">{selectedCourse.description}</p>
              <button 
                className="bg-orange-500 text-white px-6 py-3 rounded-full text-lg font-semibold hover:bg-orange-600 transition-colors duration-300"
                onClick={() => setSelectedCourse(null)}
              >
                Close
              </button>
            </div>
          </div>
        )}
      </div>
    )
}

export default Courses;
