import { useNavigate } from 'react-router-dom';
import Schools from "../assets/data/courses.json"

export default function Build() {
    const navigate = useNavigate();

    const handleSchoolClick = (schoolName) => {
        try {
            navigate(`/school/${encodeURIComponent(schoolName)}`);
        } catch (error) {
            console.error("Navigation error:", error);
            // Handle the error appropriately, e.g., show an error message to the user
        }
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen p-4">
            <div className="w-full md:w-1/2 mb-8">
                <h1 className="gradient-text font-bold text-4xl mb-4">
                    Select your school
                </h1>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {Schools.map((school, index) => (
                        <div 
                            key={index} 
                            className="bg-neutral-800 rounded-lg p-4 hover:scale-105 transition-all duration-300 cursor-pointer"
                            onClick={() => handleSchoolClick(school["school-name"])}
                        >
                            <p className="text-white">{school["school-name"]}</p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}
