import React, { useState, useEffect } from "react";

export default function Build() {
  const [currentYear, setCurrentYear] = useState(1);
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    fetch('/freshman.json')
      .then(response => response.json())
      .then(data => setCourses(data));
  }, []);

  const handleYearChange = (direction) => {
    if (direction === 'prev' && currentYear > 1) {
      setCurrentYear(currentYear - 1);
    } else if (direction === 'next' && currentYear < 4) {
      setCurrentYear(currentYear + 1);
    }
  };

  const getYearName = (year) => {
    switch (year) {
      case 1:
        return "Freshman";
      case 2:
        return "Sophomore";
      case 3:
        return "Junior";
      case 4:
        return "Senior";
      default:
        return "";
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4 bg-gray-900">
      <div className="w-full max-w-3xl mb-8">
        <h1 className="gradient-text font-bold text-5xl mb-6 text-center">
          Let's build your schedule
        </h1>
        <p className="text-white text-lg mb-8 text-center">
          Select courses you'd like to take in each year.
        </p>
        <div className="mt-8 flex justify-between items-center mb-12">
          {currentYear > 1 && (
            <button
              className="text-white bg-orange-800 px-6 py-3 rounded-lg hover:bg-orange-700 transition-all duration-300 group inline-flex items-center relative overflow-hidden text-base font-semibold"
              onClick={() => handleYearChange('prev')}
            >
              <span className="relative z-10 transition-all duration-500 group-hover:pl-6">
                BACK
              </span>
              <span className="absolute left-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:left-4">
                ←
              </span>
            </button>
          )}
          <h2 className="gradient-text font-bold text-4xl">{getYearName(currentYear)}</h2>
          {currentYear < 4 && (
            <button
              className="text-white bg-orange-800 px-6 py-3 rounded-lg hover:bg-orange-700 transition-all duration-300 group inline-flex items-center relative overflow-hidden text-base font-semibold"
              onClick={() => handleYearChange('next')}
            >
              <span className="relative z-10 transition-all duration-500 group-hover:pr-6">
                NEXT
              </span>
              <span className="absolute right-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:right-4">
                →
              </span>
            </button>
          )}
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {[...Array(8)].map((_, index) => (
            <div key={index} className="relative">
              <select 
                className="w-full p-3 bg-neutral-800 text-white rounded-lg appearance-none border border-neutral-700 focus:border-orange-500 focus:ring focus:ring-orange-200 focus:ring-opacity-50 transition-all duration-300"
              >
                <option value="">Select a course</option>
                {courses.map((course, courseIndex) => (
                  <option key={courseIndex} value={course.id}>{course.name}</option>
                ))}
              </select>
              <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-white">
                <svg className="fill-current h-4 w-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
                  <path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z"/>
                </svg>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
