import React, { useState, useEffect } from "react";
import freshmanData from '../assets/data/freshman.json';
import sophomoreData from '../assets/data/sophomore.json';
import juniorData from '../assets/data/junior.json';
import seniorData from '../assets/data/senior.json';

export default function Build() {
  const [currentYear, setCurrentYear] = useState(1);
  const [courses, setCourses] = useState([]);
  const [allSelectedCourses, setAllSelectedCourses] = useState({
    1: Array(8).fill(null),
    2: Array(8).fill(null),
    3: Array(8).fill(null),
    4: Array(8).fill(null)
  });
  const [searchTerms, setSearchTerms] = useState(Array(8).fill(''));
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [doubleBlockSelected, setDoubleBlockSelected] = useState(false);

  useEffect(() => {
    const yearData = {
      1: freshmanData["freshman-year"],
      2: sophomoreData["sophomore-year"],
      3: juniorData["junior-year"],
      4: seniorData["senior-year"]
    };
    setCourses(yearData[currentYear]);
  }, [currentYear]);

  const handleYearChange = (direction) => {
    if (direction === 'prev' && currentYear > 1) {
      setCurrentYear(currentYear - 1);
    } else if (direction === 'next' && currentYear < 4) {
      if (currentYear === 1) {
        console.log("Freshman year courses:", allSelectedCourses[1]);
      }
      setCurrentYear(currentYear + 1);
    }
    setSearchTerms(Array(8).fill(''));
    setActiveDropdown(null);
    setDoubleBlockSelected(allSelectedCourses[currentYear].some(course => course && course.name.includes("Double-Blocked")));
  };

  const getYearName = (year) => {
    switch (year) {
      case 1:
        return "Freshman";
      case 2:
        return "Sophomore";
      case 3:
        return "Junior";
      case 4:
        return "Senior";
      default:
        return "";
    }
  };

  const handleSearchChange = (index, value) => {
    const newSearchTerms = [...searchTerms];
    newSearchTerms[index] = value;
    setSearchTerms(newSearchTerms);
  };

  const handleCourseSelect = (index, course) => {
    const newSelectedCourses = [...allSelectedCourses[currentYear]];
    newSelectedCourses[index] = course;

    const isDoubleBlocked = course.name.includes("Double-Blocked");

    if (isDoubleBlocked) {
      setDoubleBlockSelected(true);
    } else if (index > 0 && newSelectedCourses[index - 1] && newSelectedCourses[index - 1].name.includes("Double-Blocked")) {
      newSelectedCourses[index] = null;
    }

    const doubleBlockIndex = newSelectedCourses.findIndex(course => course && course.name.includes("Double-Blocked"));
    if (doubleBlockIndex !== -1) {
      newSelectedCourses[7] = null;
    }

    setAllSelectedCourses(prev => ({
      ...prev,
      [currentYear]: newSelectedCourses
    }));

    const newSearchTerms = [...searchTerms];
    newSearchTerms[index] = '';
    setSearchTerms(newSearchTerms);

    setActiveDropdown(null);
  };

  const filteredCourses = (index, subject) => {
    const availableCourses = courses.flatMap(department => 
      department.courses.filter(course =>
        !allSelectedCourses[currentYear].some(selectedCourse => selectedCourse && selectedCourse.name === course.name) &&
        (subject === 'Elective' ? 
          !['Math', 'English', 'Social Studies', 'Science'].includes(department.department) :
          department.department === subject)
      )
    );
    return availableCourses.filter(course =>
      course.name.toLowerCase().includes(searchTerms[index].toLowerCase())
    );
  };

  const toggleDropdown = (index) => {
    setActiveDropdown(activeDropdown === index ? null : index);
    if (activeDropdown !== index) {
      const newSearchTerms = [...searchTerms];
      newSearchTerms[index] = '';
      setSearchTerms(newSearchTerms);
    }
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (!event.target.closest('.dropdown-container')) {
        setActiveDropdown(null);
      }
    };

    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  const renderDropdown = (index, subject) => {
    return (
      <div key={index} className="relative dropdown-container">
        <div
          onClick={() => toggleDropdown(index)}
          className="w-full p-3 bg-neutral-800 text-white rounded-lg cursor-pointer hover:bg-neutral-700 transition-all duration-300 flex justify-between items-center"
        >
          <span>{allSelectedCourses[currentYear][index] ? allSelectedCourses[currentYear][index].name : `${subject}`}</span>
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={`transform transition-transform duration-300 ${activeDropdown === index ? 'rotate-180' : ''}`}>
            <polyline points="6 9 12 15 18 9"></polyline>
          </svg>
        </div>
        {activeDropdown === index && (
          <div className="absolute z-50 w-full mt-1 bg-neutral-800 rounded-lg overflow-hidden shadow-2xl">
            <input
              type="text"
              value={searchTerms[index]}
              onChange={(e) => handleSearchChange(index, e.target.value)}
              placeholder={`Search for a ${subject}`}
              className="w-full p-3 bg-neutral-700 text-white focus:outline-none shadow-2xl"
            />
            <div className="max-h-60 overflow-auto">
              {filteredCourses(index, subject).map((course, courseIndex) => (
                <div
                  key={courseIndex}
                  className="p-2 hover:bg-neutral-700 cursor-pointer text-white"
                  onClick={() => handleCourseSelect(index, course)}
                >
                  {course.name}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    );
  };

  const handleSubmit = () => {
    console.log("All selected courses:", allSelectedCourses);
    // Here you can add logic to submit the data to a server or perform any other action
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="w-full max-w-3xl mb-8">
        <h1 className="gradient-text font-bold text-5xl mb-6 text-center">
          Let's build.
        </h1>
        <p className="text-white text-lg mb-8 text-center">
          Select your classes for the next four years!
        </p>
        <div className="mt-8 flex justify-center items-center mb-12">
          <h2 className="gradient-text font-bold text-4xl">{getYearName(currentYear)}</h2>
        </div>
        <h3 className="gradient-text text-xl mb-4 text-center font-semibold">Core Classes</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          {renderDropdown(0, 'Math')}
          {renderDropdown(1, 'English')}
          {renderDropdown(2, 'Social Studies')}
          {renderDropdown(3, 'Science')}
        </div>
        <h3 className="gradient-text text-xl mb-4 text-center font-semibold">Electives</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {renderDropdown(4, 'Elective')}
          {renderDropdown(5, 'Elective')}
          {renderDropdown(6, 'Elective')}
          {!doubleBlockSelected && renderDropdown(7, 'Elective')}
        </div>
        <div className="mt-8 flex justify-between items-center">
          {currentYear > 1 && (
            <button
              onClick={() => handleYearChange('prev')}
              className="text-white bg-orange-700 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden"
            >
              <span className="relative z-10 transition-all duration-500 group-hover:pl-6">
                BACK
              </span>
              <span className="absolute left-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:left-4">
                ←
              </span>
            </button>
          )}
          {currentYear < 4 ? (
            <button
              onClick={() => handleYearChange('next')}
              className="text-white bg-orange-700 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden"
            >
              <span className="relative z-10 transition-all duration-500 group-hover:pr-6">
                NEXT
              </span>
              <span className="absolute right-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:right-4">
                →
              </span>
            </button>
          ) : (
            <button
              onClick={handleSubmit}
              className="text-white bg-green-700 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300"
            >
              SUBMIT
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
