import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { MagnifyingGlassIcon, ChevronDownIcon, XMarkIcon } from '@heroicons/react/24/outline';

const SearchBar = ({ onSearch, onFilter }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterOption, setFilterOption] = useState('all');
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const handleSearch = (e) => {
    onSearch(e.target.value);
  };

  const handleFilter = (option) => {
    setFilterOption(option);
    onFilter(option);
    setIsDropdownOpen(false);
  };

  const filterOptions = [
    { value: 'all', label: 'All' },
    { value: 'Electives', label: 'Electives' },
    { value: 'Math', label: 'Math' },
    { value: 'English', label: 'English' },
    { value: 'Science', label: 'Science' },
    { value: 'Social Studies', label: 'Social Studies' },
    { value: 'credits-low-high', label: 'Credits (Low to High)' },
    { value: 'credits-high-low', label: 'Credits (High to Low)' },
  ];

  return (
    <div className="w-11/12 sm:w-4/6 mt-10 mb-6 flex items-center">
      <div className="relative flex-grow mr-4">
        <div className="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
          <MagnifyingGlassIcon className="w-5 h-5 text-gray-500 dark:text-gray-400" />
        </div>
        <input
          type="search"
          id="default-search"
          className="block w-full p-4 ps-10 text-sm text-gray-50 shadow-2xl rounded-lg bg-neutral-800"
          placeholder='Search for courses here...'
          value={searchTerm}
          onChange={(e) => {
            setSearchTerm(e.target.value);
            handleSearch(e);
          }}
        />
      </div>
      <div className="relative dropdown-container w-48">
        <div
          onClick={() => setIsDropdownOpen(!isDropdownOpen)}
          className="w-full p-3 bg-neutral-800 text-white rounded-lg cursor-pointer hover:bg-neutral-700 transition-all duration-300 flex justify-between items-center"
        >
          <span className="text-sm">{filterOptions.find(option => option.value === filterOption)?.label || 'Filter'}</span>
          <ChevronDownIcon className={`w-5 h-5 transform transition-transform duration-300 ${isDropdownOpen ? 'rotate-180' : ''}`} />
        </div>
        {isDropdownOpen && (
          <div className="absolute z-50 w-full mt-1 bg-neutral-800 rounded-lg overflow-hidden shadow-2xl">
            <div className="max-h-80 overflow-auto">
              {filterOptions.map((option) => (
                <div
                  key={option.value}
                  className="p-2 hover:bg-neutral-700 cursor-pointer text-white text-sm"
                  onClick={() => handleFilter(option.value)}
                >
                  {option.label}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

const Courses = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [filterOption, setFilterOption] = useState('all');
    const [filteredCourses, setFilteredCourses] = useState([]);
    const [selectedCourse, setSelectedCourse] = useState(null);
    const [allCourses, setAllCourses] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
      const fetchCourses = async () => {
        try {
          // Using Google Sheets API v4
          const SPREADSHEET_ID = '19XGBFiAJC3L4CKwxeMaAs3itA7AFeQNoNQbeJAiXZ8U';
          const RANGE = 'Sheet1!A:D'; // Adjust range according to your sheet
          const API_KEY = 'AIzaSyCxATN22mSeipVReXFJDsTleeGGkx8nDTg'; // Replace with your API key

          const response = await axios.get(
            `https://sheets.googleapis.com/v4/spreadsheets/${SPREADSHEET_ID}/values/${RANGE}?key=${API_KEY}`
          );

          const rows = response.data.values;
          const courses = rows.slice(1)
            .map(row => ({
              id: row[0],
              name: row[1],
              description: row[2],
              credits: row[3]
            }))
            .filter(course => course.name && course.credits); // Filter out courses without name or credits

          setAllCourses(courses);
          setFilteredCourses(courses);
        } catch (error) {
          console.error('Error fetching courses:', error);
          toast.error('Failed to load courses');
        }
      };

      fetchCourses();
    }, []);

    const handleSearch = (term) => {
      setSearchTerm(term);
      filterCourses(term, filterOption);
    };

    const handleFilter = (option) => {
      setFilterOption(option);
      filterCourses(searchTerm, option);
    };

    const filterCourses = (term, option) => {
      let filtered = [...allCourses];

      // Apply search term filter
      if (term) {
        filtered = filtered.filter(course =>
          course.name.toLowerCase().includes(term.toLowerCase()) ||
          course.description.toLowerCase().includes(term.toLowerCase())
        );
      }

      // Apply category/sort filter
      switch (option) {
        case 'credits-low-high':
          filtered.sort((a, b) => parseFloat(a.credits) - parseFloat(b.credits));
          break;
        case 'credits-high-low':
          filtered.sort((a, b) => parseFloat(b.credits) - parseFloat(a.credits));
          break;
        case 'all':
          break;
        default:
          filtered = filtered.filter(course => course.category === option);
      }

      setFilteredCourses(filtered);
    };

    return (
      <div className="min-h-screen bg-neutral-900 flex flex-col items-center p-4">
        <SearchBar onSearch={handleSearch} onFilter={handleFilter} />
        <div className="w-11/12 sm:w-4/6 grid grid-cols-5 gap-4">
          {filteredCourses.map((course) => (
            <div
              key={course.id}
              className="bg-neutral-800 p-4 rounded-lg shadow-lg hover:bg-neutral-700 transition-all duration-300 cursor-pointer h-48 overflow-auto"
              onClick={() => setSelectedCourse(course)}
            >
              <h3 className="text-lg font-semibold text-white">{course.name}</h3>
              <p className="text-gray-300 mt-2 text-sm">{course.description}</p>
              <p className="text-gray-400 mt-2 text-sm">Credits: {course.credits}</p>
            </div>
          ))}
        </div>
        <ToastContainer position="bottom-right" theme="dark" />
      </div>
    );
};

export default Courses;
