import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import {
  MagnifyingGlassIcon,
  ChevronDownIcon,
  XMarkIcon,
} from "@heroicons/react/24/outline";
import SearchBar from "./SearchBar";

const Courses = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filterOption, setFilterOption] = useState("all");
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [allCourses, setAllCourses] = useState([]);
  const navigate = useNavigate();

  const departmentMap = {
    English: "English Language Arts",
    Mathematics: "Mathematics",
    Science: "Science",
    "Social Studies": "Social Studies",
    LOTE: "Languages Other Than English",
    "Fine Arts": "Fine Arts",
    "Physical Education": "Physical Education",
    Dance: "Dance",
    Art: "Art",
    JROTC: "JROTC",
    "Computer Science": "Computer Science",
    Engineering: "Engineering",
    Robotics: "Robotics",
    Animation: "Animation",
    "Business Management": "Business Management & Administration",
    Debate: "Debate",
    "UIL Athletics": "UIL Athletics",
    "Misc. Courses": "Misc. Courses"
  };

  const creditMeanings = {
    0.5: "Half Credit Course",
    2: "Two Credit Course",
    3: "Three Credit Course",
    A: "Fall Semester",
    B: "Spring Semester",
    9: "Open to 9th Grade",
    10: "Open to 10th Grade",
    11: "Open to 11th Grade",
    12: "Open to 12th Grade",
    L: "Local Credit",
    "#": "See Note Below",
    "@": "Teacher Approval Required",
    D: "ACC Dual Credit",
    DR: "UT OnRamps",
    AP: "Advanced Placement",
    IB: "International Baccalaureate",
    H: "Honors",
    PreAP: "Pre-AP",
    "1FA + 1PE": "1 Fine Arts and 1 PE Credit",
    "1FA + .5PE": "1 Fine Arts and Â½ PE Credit",
  };

  const createCourse = (row, nameIndex, numberIndex, creditsIndex, department, courseLinks) => {
    const courseName = row[nameIndex]?.trim() || "";
    if (!courseName || courseName.toLowerCase().includes("course name") || 
        courseName.toLowerCase().includes("credit course") ||
        courseName.toLowerCase().includes("dual credit course")) {
      return null;
    }

    const courseType = [];
    if (courseName.includes("AP")) courseType.push("AP");
    if (courseName.includes("IB")) courseType.push("IB");
    if (courseName.includes("Honors")) courseType.push("Honors");
    if (courseName.includes("Pre-AP")) courseType.push("Pre-AP");
    if (courseName.includes("Dual")) courseType.push("Dual Credit");

    return {
      name: courseName,
      department,
      number: row[numberIndex]?.trim() || "Unknown",
      information: courseLinks[courseName] || "Unknown",
      credits: row[creditsIndex]
        ? row[creditsIndex]
            .split(",")
            .map((key) => creditMeanings[key.trim()] || key.trim())
            .join(", ")
        : "Single Credit Course",
      courseType,
      link: department,
    };
  };

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const [sheetResponse, htmlResponse] = await Promise.all([
          axios.get(`https://sheets.googleapis.com/v4/spreadsheets/19XGBFiAJC3L4CKwxeMaAs3itA7AFeQNoNQbeJAiXZ8U/values/Sheet1!A4:Z500?key=AIzaSyCxATN22mSeipVReXFJDsTleeGGkx8nDTg`),
          axios.get("https://docs.google.com/spreadsheets/u/1/d/19XGBFiAJC3L4CKwxeMaAs3itA7AFeQNoNQbeJAiXZ8U/htmlview")
        ]);

        const parser = new DOMParser();
        const doc = parser.parseFromString(htmlResponse.data, "text/html");
        const courseLinks = {};
        doc.querySelectorAll("a").forEach((link) => {
          if (link.textContent) {
            courseLinks[link.textContent.trim()] = link.href;
          }
        });

        const courses = sheetResponse.data.values.reduce((acc, row, index, array) => {
          if (!row[0] && !row[1] && !row[5]) return acc;

          let department = "";
          for (let i = index; i >= 0; i--) {
            if (array[i][0] && array[i][0].toUpperCase() === array[i][0]) {
              department = departmentMap[array[i][0]] || array[i][0];
              break;
            }
          }

          const course1 = createCourse(row, 1, 0, 2, department, courseLinks);
          const course2 = createCourse(row, 5, 4, 6, department, courseLinks);

          if (course1) acc.push(course1);
          if (course2) acc.push(course2);

          return acc;
        }, []);

        setAllCourses(courses);
      } catch (error) {
        console.error("Error fetching courses:", error);
        toast.error("Failed to load courses");
      }
    };
    fetchCourses();
  }, []);

  useEffect(() => {
    let filtered = allCourses;

    if (searchTerm) {
      filtered = filtered.filter((course) =>
        course.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (filterOption !== "all") {
      filtered = filtered.filter((course) => {
        if (["AP", "IB", "Honors", "Pre-AP", "Dual Credit"].includes(filterOption)) {
          return course.courseType.includes(filterOption);
        }
        return course.department === filterOption;
      });
    }

    setFilteredCourses(filtered);
  }, [searchTerm, filterOption, allCourses]);

  const handleCourseClick = (course) => {
    setSelectedCourse(course);
    toast("Scroll down to see all course information!", {
      position: "top-center",
      autoClose: 3000,
      theme: "dark",
      style: {
        background: "#262626",
        color: "#fff",
        borderRadius: "8px",
        boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
        fontSize: "14px",
      },
      progressStyle: { background: "#FF8C00" },
    });
  };

  const badgeColors = {
    "English Language Arts": "bg-purple-100 text-purple-800 ring-purple-800/20",
    Mathematics: "bg-blue-100 text-blue-800 ring-blue-800/20",
    Science: "bg-green-100 text-green-800 ring-green-700/30",
    "Social Studies": "bg-yellow-100 text-yellow-900 ring-yellow-700/30",
    "Languages Other Than English": "bg-pink-100 text-pink-800 ring-pink-800/20",
    "Fine Arts": "bg-indigo-100 text-indigo-800 ring-indigo-800/20",
    "Physical Education": "bg-orange-100 text-orange-800 ring-orange-800/20",
    Dance: "bg-rose-100 text-rose-800 ring-rose-800/20",
    Art: "bg-fuchsia-100 text-fuchsia-800 ring-fuchsia-800/20",
    JROTC: "bg-slate-100 text-slate-800 ring-slate-800/20",
    "Computer Science": "bg-lime-100 text-lime-800 ring-lime-800/20",
    Engineering: "bg-sky-100 text-sky-800 ring-sky-800/20",
    Robotics: "bg-emerald-100 text-emerald-800 ring-emerald-800/20",
    Animation: "bg-amber-100 text-amber-800 ring-amber-800/20",
    "Business Management & Administration": "bg-red-100 text-red-800 ring-red-800/20",
    Debate: "bg-violet-100 text-violet-800 ring-violet-800/20",
    "UIL Athletics": "bg-cyan-100 text-cyan-800 ring-cyan-800/20",
    "Misc. Courses": "bg-neutral-100 text-neutral-800 ring-neutral-800/20",
    AP: "bg-emerald-100 text-emerald-800 ring-emerald-800/20",
    IB: "bg-cyan-100 text-cyan-800 ring-cyan-800/20",
    Honors: "bg-amber-100 text-amber-800 ring-amber-800/20",
    "Pre-AP": "bg-teal-100 text-teal-800 ring-teal-800/20",
    "Dual Credit": "bg-violet-100 text-violet-800 ring-violet-800/20",
  };

  return (
    <div className="min-h-screen flex flex-col items-center bg-neutral-900 px-4">
      <SearchBar
        onSearch={setSearchTerm}
        onFilter={setFilterOption}
        filterOptions={[...Object.values(departmentMap), "AP", "IB", "Honors", "Pre-AP", "Dual Credit"]}
      />
      <div className="w-full max-w-4xl rounded-lg p-6 mb-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {filteredCourses.map((course, i) => (
            <div
              key={i}
              className="bg-neutral-800 p-4 rounded-lg hover:scale-105 transition-transform duration-200 cursor-pointer"
              onClick={() => handleCourseClick(course)}
            >
              <h3 className="text-lg font-semibold gradient-text">
                {course.name}
              </h3>
              <div className="flex flex-wrap gap-2 mt-2">
                <span
                  className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${
                    badgeColors[course.department] ||
                    "bg-gray-100 text-gray-700 ring-gray-600/20"
                  }`}
                >
                  {course.department}
                </span>
                {course.courseType.map((type, index) => (
                  <span
                    key={index}
                    className={`inline-flex items-center rounded-md px-2 py-1 text-xs font-medium ring-1 ring-inset ${badgeColors[type]}`}
                  >
                    {type}
                  </span>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
      {selectedCourse && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 overflow-y-auto">
          <div className="bg-neutral-800 p-8 rounded-lg max-w-4xl w-full shadow-2xl transform transition-all duration-300 ease-in-out my-8">
            <h2 className="text-3xl font-bold mb-6 gradient-text">
              {selectedCourse.name}
            </h2>
            <div className="space-y-4 text-neutral-300 max-h-[70vh] overflow-y-auto pr-4">
              <p>
                <span className="font-semibold">Number:</span>{" "}
                {selectedCourse.number || "N/A"}
              </p>
              <p>
                <span className="font-semibold">Key:</span>{" "}
                {selectedCourse.credits || "N/A"}
              </p>
              <p>
                <span className="font-semibold">Department:</span>{" "}
                {selectedCourse.department || "N/A"}
              </p>
              <p>
                <span className="font-semibold">Course Type:</span>{" "}
                {selectedCourse.courseType.join(", ") || "Regular"}
              </p>
              <p>
                <br />
                <span className="font-bold">Information</span> <br />
                <br />
                <iframe
                  src={selectedCourse.information}
                  title="Course Information"
                  className="w-full h-[600px] border-0 rounded"
                ></iframe>
              </p>
            </div>
            <div className="mt-8 flex justify-end">
              <button
                onClick={() => setSelectedCourse(null)}
                className="text-white bg-orange-700 p-3 rounded-md hover:rotate-[15deg] hover:bg-orange-600 transition-all duration-300 group inline-flex items-center justify-center relative overflow-hidden"
              >
                <span className="relative z-10 transition-all duration-500 transform">
                  <XMarkIcon className="h-6 w-6" />
                </span>
              </button>
            </div>
          </div>
        </div>
      )}
      <ToastContainer />
    </div>
  );
};

export default Courses;
