import React, { useState, useEffect } from "react";
import freshmanData from '../assets/data/freshman.json';

export default function Build() {
  const [currentYear, setCurrentYear] = useState(1);
  const [courses, setCourses] = useState([]);
  const [selectedCourses, setSelectedCourses] = useState(Array(8).fill(''));
  const [searchTerms, setSearchTerms] = useState(Array(8).fill(''));
  const [dropdownOpen, setDropdownOpen] = useState(Array(8).fill(false));

  useEffect(() => {
    setCourses(freshmanData["freshman-year"]);
  }, []);

  const handleYearChange = (direction) => {
    if (direction === 'prev' && currentYear > 1) {
      setCurrentYear(currentYear - 1);
    } else if (direction === 'next' && currentYear < 4) {
      setCurrentYear(currentYear + 1);
    }
  };

  const getYearName = (year) => {
    switch (year) {
      case 1:
        return "Freshman";
      case 2:
        return "Sophomore";
      case 3:
        return "Junior";
      case 4:
        return "Senior";
      default:
        return "";
    }
  };

  const handleSearchChange = (index, value) => {
    const newSearchTerms = [...searchTerms];
    newSearchTerms[index] = value;
    setSearchTerms(newSearchTerms);

    const newDropdownOpen = [...dropdownOpen];
    newDropdownOpen[index] = true;
    setDropdownOpen(newDropdownOpen);
  };

  const handleCourseSelect = (index, course) => {
    const newSelectedCourses = [...selectedCourses];
    newSelectedCourses[index] = course;
    setSelectedCourses(newSelectedCourses);

    const newSearchTerms = [...searchTerms];
    newSearchTerms[index] = `${course.number} - ${course.name}`;
    setSearchTerms(newSearchTerms);

    const newDropdownOpen = [...dropdownOpen];
    newDropdownOpen[index] = false;
    setDropdownOpen(newDropdownOpen);
  };

  const filteredCourses = (index) => {

    const allCourses = courses.flatMap(department => 
      department.courses.filter(course =>
        `${course.number} - ${course.name}`.toLowerCase().includes(searchTerms[index].toLowerCase())
      )
    );

    const coreCourses = allCourses.filter(course => 
      ['math', 'science', 'social studies', 'english'].includes(course.department.toLowerCase())
    );

    const electiveCourses = allCourses.filter(course => 
      !['math', 'science', 'social studies', 'english'].includes(course.department.toLowerCase())
    );

    return { coreCourses, electiveCourses };
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="w-full max-w-3xl mb-8">
        <h1 className="gradient-text font-bold text-5xl mb-6 text-center">
          Let's build.
        </h1>
        <p className="text-white text-lg mb-8 text-center">
          Select courses you'd like to take in each year.
        </p>
        <div className="mt-8 flex justify-between items-center mb-12">
          {currentYear > 1 && (
            <button
              className="text-white bg-orange-800 px-6 py-3 rounded-lg hover:bg-orange-700 transition-all duration-300 group inline-flex items-center relative overflow-hidden text-base font-semibold outline-none"
              onClick={() => handleYearChange('prev')}
            >
              <span className="relative z-10 transition-all duration-500 group-hover:pl-6">
                BACK
              </span>
              <span className="absolute left-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:left-4">
                ←
              </span>
            </button>
          )}
          <h2 className="gradient-text font-bold text-4xl">{getYearName(currentYear)}</h2>
          {currentYear < 4 && (
            <button
              className="text-white bg-orange-800 px-6 py-3 rounded-lg hover:bg-orange-700 transition-all duration-300 group inline-flex items-center relative overflow-hidden text-base font-semibold outline-none"
              onClick={() => handleYearChange('next')}
            >
              <span className="relative z-10 transition-all duration-500 group-hover:pr-6">
                NEXT
              </span>
              <span className="absolute right-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:right-4">
                →
              </span>
            </button>
          )}
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {[...Array(8)].map((_, index) => (
            <div key={index} className="relative">
              <input
                type="text"
                value={searchTerms[index]}
                onChange={(e) => handleSearchChange(index, e.target.value)}
                placeholder="Search for a course"
                className="w-full p-3 bg-neutral-800 text-white rounded-lg border border-neutral-700 focus:border-orange-500 transition-all duration-300 outline-none"
              />
              {dropdownOpen[index] && (


                <div className="absolute z-10 w-full mt-1 bg-neutral-800 border border-neutral-700 rounded-lg max-h-80 overflow-auto shadow-lg">
                  <div className="p-2 bg-neutral-700 text-white font-semibold">Core Courses</div>
                  {filteredCourses(index).coreCourses.map((course, courseIndex) => (
                    <div
                      key={courseIndex}

                      className="p-2 hover:bg-neutral-700 cursor-pointer text-white transition-colors duration-200"
                      onClick={() => handleCourseSelect(index, course)}
                    >
                      {course.number} - {course.name}
                    </div>
                  ))}
                  <div className="p-2 bg-neutral-700 text-white font-semibold mt-2">Electives</div>
                  {filteredCourses(index).electiveCourses.map((course, courseIndex) => (
                    <div
                      key={courseIndex}
                      className="p-2 hover:bg-neutral-700 cursor-pointer text-white transition-colors duration-200"
                      onClick={() => handleCourseSelect(index, course)}
                    >
                      {course.number} - {course.name}
                    </div>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
