import { useState, useEffect } from "react";

export default function Build() {
  const [selectedInterests, setSelectedInterests] = useState([]);
  const [showChallengeQuestion, setShowChallengeQuestion] = useState(false);
  const [showInterests, setShowInterests] = useState(true);
  const [challengeResponse, setChallengeResponse] = useState(null);
  const [fourYearPlan, setFourYearPlan] = useState(null);
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    fetch('/src/assets/data/courses.json')
      .then(response => response.json())
      .then(data => {
        setCourses(data);
      })
      .catch(error => console.error('Error fetching course data:', error));
  }, []);

  const interests = [
    { id: "art", name: "Art" },
    { id: "theatre", name: "Theatre" },
    { id: "dance", name: "Dance" },
    { id: "music", name: "Music" },
    { id: "journalism", name: "Journalism" },
    { id: "jrotc", name: "Junior Reserve Officer Training Corps" },
    { id: "fashion", name: "Fashion Design" },
    { id: "cs", name: "Computer Science" },
    { id: "engineering", name: "Engineering" },
    { id: "robotics", name: "Robotics" },
    { id: "finance", name: "Finance" },
    { id: "graphics", name: "Graphic Design" },
    { id: "marketing", name: "Marketing" },
    { id: "video", name: "Video Production" },
    { id: "animation", name: "Animation" },
    { id: "business", name: "Business Management & Administration" },
    { id: "debate", name: "Debate" },
    { id: "biomedical", name: "Biomedical Science" },
    { id: "health", name: "Health Science" },
    { id: "sports", name: "Sports" },
  ];

  const toggleInterest = (id) => {
    setSelectedInterests((prevSelected) => {
      let newSelected;
      if (prevSelected.includes(id)) {
        newSelected = prevSelected.filter((item) => item !== id);
      } else if (prevSelected.length < 3) {
        newSelected = [...prevSelected, id];
      } else {
        newSelected = prevSelected;
      }
      return newSelected;
    });
  };

  const handleSubmit = () => {
    if (selectedInterests.length > 0) {
      console.log("Selected interests:", selectedInterests);
      setShowChallengeQuestion(true);
      setShowInterests(false);
    } else {
      alert("Please select at least one interest before submitting.");
    }
  };

  const handleChallengeResponse = (response) => {
    console.log("Challenge response:", response);
    setChallengeResponse(response);
    buildFourYearPlan(selectedInterests, response);
  };

  const handleBack = () => {
    if (showChallengeQuestion) {
      setShowChallengeQuestion(false);
      setShowInterests(true);
      setChallengeResponse(null);
      setFourYearPlan(null);
    }
  };

  const buildFourYearPlan = (interests, isChallenge) => {
    const plan = {
      freshman: [],
      sophomore: [],
      junior: [],
      senior: []
    };

    interests.forEach(interest => {
      const interestCourses = courses.filter(course => course.department === interest);
      const challengeCourses = interestCourses.filter(course => course.level === (isChallenge ? "AP" : "On-Level"));

      for (let year = 9; year <= 12; year++) {
        const yearCourses = challengeCourses.filter(course => course.year === year);
        if (yearCourses.length > 0) {
          const randomCourse = yearCourses[Math.floor(Math.random() * yearCourses.length)];
          switch (year) {
            case 9:
              plan.freshman.push(randomCourse);
              break;
            case 10:
              plan.sophomore.push(randomCourse);
              break;
            case 11:
              plan.junior.push(randomCourse);
              break;
            case 12:
              plan.senior.push(randomCourse);
              break;
          }
        }
      }
    });

    setFourYearPlan(plan);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="w-full md:w-1/2 mb-8">
        {showInterests && (
          <>
            <h1 className="gradient-text font-bold text-4xl mb-4">
              Let's find your interests first.
            </h1>
            <p className="text-white mb-8">
              What fields do you enjoy? (click on up to three interests)
            </p>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {interests.map((interest) => (
                <div
                  key={interest.id}
                  className={`bg-neutral-800 rounded-lg shadow-lg hover:scale-105 transition-all cursor-pointer ${
                    selectedInterests.includes(interest.id)
                      ? "border-2 border-orange-400"
                      : ""
                  }`}
                  onClick={() => toggleInterest(interest.id)}
                >
                  <div className="p-4">
                    <h3 className="gradient-text font-semibold text-lg">
                      {interest.name}
                    </h3>
                    <p className="text-white"></p>
                  </div>
                </div>
              ))}
            </div>
            <div className="mt-8 text-center">
              <a href="" className="gradient-text">
                I don't know!
              </a>
            </div>
            <div className="mt-8 flex justify-center">
              <button
                onClick={handleSubmit}
                className="text-white bg-orange-800 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden text-sm sm:text-base"
              >
                <span className="relative z-10 transition-all duration-500 group-hover:pr-6">
                  NEXT
                </span>
                <span className="absolute right-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:right-4">
                  →
                </span>
              </button>
            </div>
          </>
        )}
        {showChallengeQuestion && (
          <>
            <h1 className="gradient-text font-bold text-4xl mb-4">
              How hard?
            </h1>
            <p className="text-white mb-8">
              Do you want to challenge yourself with tough courses (i.e., AP or
              IB) or stick with on-level courses?
            </p>
            <div className="flex flex-col sm:flex-row items-center justify-center sm:justify-start space-y-4 sm:space-y-0 sm:space-x-4">
              <button
                onClick={() => handleChallengeResponse(true)}
                className="text-white bg-orange-800 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 text-sm sm:text-base"
              >
                YES, CHALLENGE ME!
              </button>
              <button
                onClick={() => handleChallengeResponse(false)}
                className="text-white bg-orange-800 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 text-sm sm:text-base"
              >
                NO, STICK WITH ON-LEVEL
              </button>
            </div>
            <div className="mt-8 flex justify-center sm:justify-start">
              <button
                onClick={handleBack}
                className="text-white bg-orange-800 px-4 py-2 rounded-lg hover:scale-110 transition-all duration-300 group inline-flex items-center relative overflow-hidden text-sm sm:text-base"
              >
                <span className="relative z-10 transition-all duration-500 group-hover:pl-6">
                  BACK
                </span>
                <span className="absolute left-0 top-1/2 transform -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-all duration-500 group-hover:left-4">
                  ←
                </span>
              </button>
            </div>
          </>
        )}
        {fourYearPlan && (
          <div className="mt-8">
            <h2 className="gradient-text font-bold text-3xl mb-4">Your Four Year Plan</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {Object.entries(fourYearPlan).map(([year, courses]) => (
                <div key={year} className="bg-neutral-800 rounded-lg p-4">
                  <h3 className="gradient-text font-semibold text-xl mb-2 capitalize">{year}</h3>
                  <ul className="text-white">
                    {courses.map((course, index) => (
                      <li key={index}>{course.name}</li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
