import React, { useState, useEffect } from 'react';
import courseData from "../assets/data/other-three.json";

const SearchBar = ({ onSearch, onFilter }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [filterOption, setFilterOption] = useState('all');

    const handleSearch = (e) => {
      onSearch(e.target.value);
    };

    const handleFilter = (e) => {
      setFilterOption(e.target.value);
      onFilter(e.target.value);
    };

    return (
      <div className="w-11/12 sm:w-4/6 mt-10 mb-6 flex items-center">
        <div className="relative flex-grow mr-4">
          <div className="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
            <svg
              className="w-4 h-4 text-gray-500 dark:text-gray-400"
              aria-hidden="true"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 20 20"
            >
              <path
                stroke="currentColor"
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="m19 19-4-4m0-7A7 7 0 1 1 1 8a7 7 0 0 1 14 0Z"
              />
            </svg>
          </div>
          <input
            type="search"
            id="default-search"
            className="block w-full p-4 ps-10 text-sm text-gray-50 shadow-2xl rounded-lg bg-neutral-700"
            placeholder='Search for courses here...'
            value={searchTerm}
            onChange={(e) => {
              setSearchTerm(e.target.value);
              handleSearch(e);
            }}
          />
        </div>
        <select
          className="p-4 text-sm text-gray-50 shadow-2xl rounded-lg bg-neutral-700"
          value={filterOption}
          onChange={handleFilter}
        >
          <option value="all">All Courses</option>
          <option value="1">1 Credit</option>
          <option value="2">2 Credits</option>
          <option value="3">3 Credits</option>
          <option value="4">4 Credits</option>
        </select>
      </div>
    );
};

const Courses = () => {
      const [searchTerm, setSearchTerm] = useState('');
      const [filterOption, setFilterOption] = useState('all');
      const [filteredCourses, setFilteredCourses] = useState([]);

      useEffect(() => {
          const allCourses = courseData["sophomore-year"].flatMap(department => department.courses);
          const filtered = allCourses.filter(course =>
              (course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
              course.number.toLowerCase().includes(searchTerm.toLowerCase())) &&
              (filterOption === 'all' || course.credits.toString() === filterOption)
          );

          setFilteredCourses(filtered);
      }, [searchTerm, filterOption]);

      const handleSearch = (term) => {
          setSearchTerm(term);
      };

      const handleFilter = (option) => {
          setFilterOption(option);
      };

      return (
          <div className="min-h-screen flex flex-col items-center bg-neutral-900 px-4">
              <SearchBar onSearch={handleSearch} onFilter={handleFilter} />
              <div className="w-full max-w-4xl rounded-lg p-6 mb-6">
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                      {filteredCourses.map((course, courseIndex) => (
                          <div key={courseIndex} className="bg-neutral-800 p-4 rounded-lg">
                              <h3 className="text-lg font-semibold gradient-text">{course.name}</h3>
                              <p className="text-neutral-300">Number: {course.number}</p>
                              <p className="text-neutral-300">Credit(s): {course.credits}</p>
                          </div>
                      ))}
                  </div>
              </div>
          </div>
      )
}

export default Courses;
