import React, { useState, useCallback, useMemo } from "react";
import PropTypes from "prop-types";
import courseListData from "../assets/data/course-list.json";
import CourseDropdown from "./CourseDropdown";

CourseDropdown.propTypes = {
  index: PropTypes.number.isRequired,
  subject: PropTypes.string.isRequired,
  courses: PropTypes.array.isRequired,
  searchTerm: PropTypes.string.isRequired,
  onSearchChange: PropTypes.func.isRequired,
  onCourseSelect: PropTypes.func.isRequired,
  selectedCourse: PropTypes.object,
  isActive: PropTypes.bool.isRequired,
  onToggle: PropTypes.func.isRequired,
  disabled: PropTypes.bool,
};

export default function Build({ courses: initialCourses }) {
  const [currentYear, setCurrentYear] = useState(1);
  const [courses, setCourses] = useState(initialCourses);
  const [allSelectedCourses, setAllSelectedCourses] = useState(
    Object.fromEntries([1, 2, 3, 4].map((year) => [year, Array(16).fill(null)]))
  );
  const [searchTerms, setSearchTerms] = useState(Array(16).fill(""));
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [doubleBlockedCourses, setDoubleBlockedCourses] = useState(
    Object.fromEntries([1, 2, 3, 4].map((year) => [year, 0]))
  );
  const [isReviewing, setIsReviewing] = useState(false);
  const [summerCourses, setSummerCourses] = useState({
    2: null,
    3: null,
    4: null,
  });
  const [errors, setErrors] = useState(
    Object.fromEntries([1, 2, 3, 4].map((year) => [year, []]))
  );
  const [disabledElectiveIndices, setDisabledElectiveIndices] = useState([]);
  const [excludedSubjects, setExcludedSubjects] = useState([]);

  React.useEffect(() => {
    setCourses(courseListData);
  }, []);

  const getYearName = (year) =>
    ["", "Freshman", "Sophomore", "Junior", "Senior"][year];

  const handleSearchChange = useCallback((index, value) => {
    setSearchTerms((prev) => ({ ...prev, [index]: value }));
  }, []);

  const isHalfSemesterCourse = (course) => {
    if (!course?.key) return false;
    return (
      (course.key.includes(".5") || course.key.includes("0.5")) ||
      (course.name.toLowerCase().startsWith("english") && course.key.includes("S2"))
    );
  };

  const handleCourseSelect = (index, course) => {
    setAllSelectedCourses((prev) => {
      const newCourses = [...prev[currentYear]];
      const oldCourse = newCourses[index];
      newCourses[index] = course;

      const dbCount = course?.name?.includes("DB")
        ? doubleBlockedCourses[currentYear] + 1
        : oldCourse?.name?.includes("DB")
        ? doubleBlockedCourses[currentYear] - 1
        : doubleBlockedCourses[currentYear];

      setDoubleBlockedCourses((prev) => ({ ...prev, [currentYear]: dbCount }));

      const newDisabledIndices = [7, 6, 5, 4]
        .slice(0, dbCount)
        .filter((idx) => !newCourses[idx] || idx === index);
      setDisabledElectiveIndices(newDisabledIndices);

      return { ...prev, [currentYear]: newCourses };
    });

    if (course) {
      const subject = course.name.split(" ")[0].toLowerCase();
      setExcludedSubjects((prev) => [...new Set([...prev, subject])]);
      setAllSelectedCourses((prev) => {
        const updatedCourses = { ...prev };
        Object.keys(updatedCourses).forEach((year) => {
          updatedCourses[year] = updatedCourses[year].map((c, i) =>
            c && i !== index && c.name.toLowerCase().startsWith(subject)
              ? null
              : c
          );
        });
        return updatedCourses;
      });
    }

    setSearchTerms((prev) => ({ ...prev, [index]: "" }));
    setActiveDropdown(null);
  };

  const handleYearChange = (direction) => {
    const newYear = direction === "prev" ? currentYear - 1 : currentYear + 1;
    if (newYear >= 1 && newYear <= 4) {
      setCurrentYear(newYear);
      setSearchTerms(Array(16).fill(""));
      setActiveDropdown(null);
      setDisabledElectiveIndices([]);
    }
  };

  const getAllCourses = useMemo(
    () => (courses ? Object.values(courses).flat() : []),
    [courses]
  );

  const filteredCourses = useMemo(
    () =>
      (index, subject, isSemesterTwo = false) => {
        if (!courses) return [];
        const availableCourses = courses[subject.toLowerCase()] || [];
        return availableCourses.filter((course) => {
          const courseSubject = course.name.split(" ")[0].toLowerCase();
          const isSubjectAllowed =
            !excludedSubjects.includes(courseSubject) ||
            allSelectedCourses[currentYear][index]?.name
              ?.toLowerCase()
              .startsWith(courseSubject);
          const isSemesterMatch = isSemesterTwo
            ? course.key.includes("S2") || isHalfSemesterCourse(course)
            : !course.key.includes("S2");
          return isSubjectAllowed && isSemesterMatch;
        });
      },
    [courses, excludedSubjects, allSelectedCourses, currentYear]
  );

  const renderDropdown = (index, subject) => {
    const isDisabled = disabledElectiveIndices.includes(index);
    const selectedCourse = allSelectedCourses[currentYear][index];
    const isSemesterCourse = isHalfSemesterCourse(selectedCourse) || (selectedCourse?.name?.toLowerCase().startsWith("english"));

    return (
      <div
        className={`flex flex-col ${
          isDisabled ? "opacity-50 bg-neutral-900" : ""
        }`}
      >
        <CourseDropdown
          key={index}
          index={index}
          subject={subject}
          courses={
            subject === "All Courses"
              ? getAllCourses
              : filteredCourses(index, subject)
          }
          searchTerm={searchTerms[index]}
          onSearchChange={handleSearchChange}
          onCourseSelect={handleCourseSelect}
          selectedCourse={selectedCourse}
          isActive={activeDropdown === index}
          onToggle={() =>
            !isDisabled &&
            setActiveDropdown(activeDropdown === index ? null : index)
          }
          disabled={isDisabled}
        />
        {isSemesterCourse && (
          <div className="mt-2 ml-4 border-l-2 border-orange-500 pl-4">
            <CourseDropdown
              key={index + 8}
              index={index + 8}
              subject={subject}
              courses={
                subject === "All Courses"
                  ? getAllCourses
                  : filteredCourses(index + 8, subject, true)
              }
              searchTerm={searchTerms[index + 8]}
              onSearchChange={handleSearchChange}
              onCourseSelect={handleCourseSelect}
              selectedCourse={allSelectedCourses[currentYear][index + 8]}
              isActive={activeDropdown === index + 8}
              onToggle={() =>
                !isDisabled &&
                setActiveDropdown(
                  activeDropdown === index + 8 ? null : index + 8
                )
              }
              disabled={isDisabled}
            />
          </div>
        )}
      </div>
    );
  };

  if (isReviewing) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen p-4 bg-neutral-900">
        <div className="w-full max-w-4xl bg-neutral-800 rounded-lg shadow-xl p-8">
          <h1 className="gradient-text font-bold text-5xl mb-8 text-center">
            Review Your Four Year Plan
          </h1>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {[1, 2, 3, 4].map((year) => (
              <div key={year} className="bg-neutral-700 rounded-lg p-6">
                <h2 className="gradient-text font-bold text-3xl mb-4">
                  {getYearName(year)} Year
                </h2>
                {summerCourses[year] && (
                  <p className="text-white mb-2">
                    Summer course: {summerCourses[year]}
                  </p>
                )}
                <ul className="list-disc list-inside text-white space-y-2">
                  {allSelectedCourses[year].slice(0, 8).map((course, i) => (
                    <li key={i} className="text-lg">
                      {course?.name || "Not selected"}
                      {isHalfSemesterCourse(course) && (
                        <div className="ml-4 text-orange-400">
                          Second Semester:{" "}
                          {allSelectedCourses[year][i + 8]?.name ||
                            "Not selected"}
                        </div>
                      )}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="w-full max-w-3xl mb-8">
        <h1 className="gradient-text font-bold text-5xl mb-6 text-center">
          Let's build.
        </h1>
        <p className="text-white text-lg mb-8 text-center">
          Select your classes for the next four years!
        </p>
        <div className="mt-8 flex justify-center items-center mb-12">
          <h2 className="gradient-text font-bold text-4xl">
            {getYearName(currentYear)}
          </h2>
        </div>
        <h3 className="gradient-text text-xl mb-4 text-center font-semibold">
          Core Classes
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          {["Math", "English", "Social Studies", "Science"].map((subject, i) =>
            renderDropdown(i, subject)
          )}
        </div>
        <h3 className="gradient-text text-xl mb-4 text-center font-semibold">
          Electives
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {[4, 5, 6, 7].map((i) => renderDropdown(i, "Elective"))}
        </div>
        <div className="mt-8 flex justify-between items-center">
          {currentYear > 1 && (
            <button
              onClick={() => handleYearChange("prev")}
              className="text-white bg-orange-700 px-4 py-2 rounded-lg hover:bg-orange-600 transition-all duration-300"
            >
              Back
            </button>
          )}
          {currentYear < 4 ? (
            <button
              onClick={() => handleYearChange("next")}
              className="text-white bg-orange-700 px-4 py-2 rounded-lg hover:bg-orange-600 transition-all duration-300"
            >
              Next
            </button>
          ) : (
            <button
              onClick={() => setIsReviewing(true)}
              className="text-white bg-orange-700 px-4 py-2 rounded-lg hover:bg-orange-600 transition-all duration-300"
            >
              Review
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
