import React, { useState, useEffect, useCallback, useMemo } from "react";
import PropTypes from 'prop-types';
import courseListData from "../assets/data/course-list.json";
import { ChevronDownIcon } from '@heroicons/react/24/solid';
import emailjs from 'emailjs-com';

// ... (rest of the code remains unchanged until the handleSendEmail function)

const handleSendEmail = async () => {
  setIsSending(true);
  setSendStatus('');
  try {
    const templateParams = {
      to_email: email,
      freshman_year: allSelectedCourses[1].map(course => course ? course.name : 'Not selected').join(', '),
      sophomore_year: allSelectedCourses[2].map(course => course ? course.name : 'Not selected').join(', '),
      junior_year: allSelectedCourses[3].map(course => course ? course.name : 'Not selected').join(', '),
      senior_year: allSelectedCourses[4].map(course => course ? course.name : 'Not selected').join(', ')
    };

    await emailjs.send(
      'YOUR_SERVICE_ID',
      'YOUR_TEMPLATE_ID',
      templateParams,
      'YOUR_USER_ID'
    );

    setSendStatus('Success! Your four year plan has been sent to your email.');
    setTimeout(() => {
      setIsReviewing(false);
      setEmail('');
      setSendStatus('');
    }, 3000);
  } catch (error) {
    console.error('Failed to send email:', error);
    setSendStatus('Failed to send email. Please try again.');
  } finally {
    setIsSending(false);
  }
};

